INSERT INTO `tp_archives` VALUES ('441', '风速仪', '型号：9535/9535A/9545/9545A', '美国TSI9535 和美国TSI9545 型风速表（风速仪），具有一台普通仪表价位的多功能仪表。该风速表（风速仪）可以使用一个具有多个传感器的探头同时测量和记录通空气中的多个参数。可以测量风速、温度和计算风量。美国TSI9545 型还可以测量相对湿度，并计算出露点温度和湿球温度。美国TSI9535 和美国TSI9535-A 型具有可伸缩的直探头。', '0', '', '', '', '', '20210716\3b924a5372ad18f7dd25fd67f7f68e62.jpg', '', '186', '', '359', '<p><img src="/zyhk/kbcms/ueditor/php/upload/image/20210716/1626407497118156.jpg" title="1626407497118156.jpg" alt="9535、9545.jpg"/></p>', '65', '1623081600', '609', '0', '1', '', '', '0');
