INSERT INTO `tp_archives` VALUES ('513', '便携式激光粒子计数器 ', '型号：9110-01', 'TSI公司9110型便携式粒子计数器可精确测量到0.100微米的粒子。它所使用的氨氖激光技术可以极大程度提高信噪比。9110型完全遵从ISO-14644-1标准，是验证一级和二级洁净房间的最佳选择。这款仪器可应用在洁净房间的监测，过程监控和过滤膜测试。', '0', '', '', '', '', '20210716\212d74af044cea4efd0aacb3e09e1600.jpg', '', '169', '', '369', '<p><img src="/zyhk/kbcms/ueditor/php/upload/image/20210716/1626414372930992.jpg" title="1626414372930992.jpg" alt="9110-01_01.jpg"/></p>', '65', '1626278400', '306', '0', '1', '', '', '0');
