INSERT INTO `tp_archives` VALUES ('460', '风速仪 ', '型号：9515', '美国TSI9515 手持式经济型数字风速计，具有非常高的精确度和准确度。专业技术人员用它做为生物安全柜面风速、喷淋室或IAQ 和通风系统的理想测试仪器。美国TSI9515 风速仪的整体式、可伸缩探头长101.6cm，用于测试一般探头难以到达的地点。', '0', '', '', '', '', '20210716\66854fa78a7d8842222c171b892bb41b.jpg', '', '204', '', '359', '<p><img src="/zyhk/kbcms/ueditor/php/upload/image/20210716/1626407532124968.jpg" title="1626407532124968.jpg" alt="9515.jpg"/></p>', '65', '1623340800', '610', '0', '1', '', '', '0');
INSERT INTO `tp_archives` VALUES ('543', '实时气溶胶监测解决方案', '', '气溶胶监测仪-通常被称为粉尘监测器，颗粒物监测，光散射激光光度计，和浊度仪 -用于测量粉尘，烟，雾，烟，雾凝聚。TSI气溶胶监测仪实时报告，直接读取结果。', '0', '', '', '', '', '20210716\b58f74841890ad08fe310236b1d87e92.jpg', '', '172', '<h3 style="box-sizing: inherit; margin: 0px 0px 24px; padding: 0px; font-weight: 400; font-family: brandon-grotesque, Arial, sans-serif; text-transform: uppercase; font-size: 1.3rem; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255);">实时气溶胶监测解决方案</h3><p style="box-sizing: inherit; margin-top: 0px; margin-bottom: 1.5rem; padding: 0px; color: rgb(51, 51, 51); font-family: rooney-web, Arial, serif; font-size: 16px; white-space: normal; background-color: rgb(255, 255, 255);">&nbsp; &nbsp; &nbsp; 气溶胶监测仪-通常被称为粉尘监测器，颗粒物监测，光散射激光光度计，和浊度仪 -用于测量粉尘，烟，雾，烟，雾凝聚。TSI气溶胶监测仪实时报告，直接读取结果，它正迅速成为行业的最佳实践，职业卫生，室内空气质量，和室外环境的排放监测。</p><p style="box-sizing: inherit; margin-top: 0px; margin-bottom: 1.5rem; padding: 0px; color: rgb(51, 51, 51); font-family: rooney-web, Arial, serif; font-size: 16px; white-space: normal; background-color: rgb(255, 255, 255);">直读气溶胶的监测与分析</p><p style="box-sizing: inherit; margin-top: 0px; margin-bottom: 1.5rem; padding: 0px; color: rgb(51, 51, 51); font-family: rooney-web, Arial, serif; font-size: 16px; white-space: normal; background-color: rgb(255, 255, 255);">气溶胶监测仪使实时，直读气溶胶的监测与分析在室内和室外的各种应用，包括：</p><ul style="box-sizing: inherit; margin-bottom: 24px; margin-left: 1rem; padding: 0px; color: rgb(51, 51, 51); font-family: rooney-web, Arial, serif; font-size: 16px; white-space: normal; background-color: rgb(255, 255, 255);" class=" list-paddingleft-2"><li><p>粉尘监测</p></li><li><p>环境监测</p></li><li><p>工作区监测</p></li><li><p>个人暴露监测</p></li><li><p>户外环境逃犯排放监测</p></li><li><p>暖通空调和利监测</p></li><li><p>室内空气质量监测</p></li></ul><p style="box-sizing: inherit; margin-top: 0px; margin-bottom: 1.5rem; padding: 0px; color: rgb(51, 51, 51); font-family: rooney-web, Arial, serif; font-size: 16px; white-space: normal; background-color: rgb(255, 255, 255);">区域监测和呼吸区分析解决方案</p><ul style="box-sizing: inherit; margin-bottom: 24px; margin-left: 1rem; padding: 0px; color: rgb(51, 51, 51); font-family: rooney-web, Arial, serif; font-size: 16px; white-space: normal; background-color: rgb(255, 255, 255);" class=" list-paddingleft-2"><li><p>TSI DUSTTRAK™DRX气溶胶监测仪同时测量的质量和体积分数，并提供气溶胶的监测与分析-所有在一个仪器&nbsp;</p></li><li><p>TSI划分™am510个人气溶胶监测仪-轻个人粉尘监测对市场是一个坚固的带安装激光光度计，结构紧凑，安静。</p></li></ul><p style="box-sizing: inherit; margin-top: 0px; margin-bottom: 1.5rem; padding: 0px; color: rgb(51, 51, 51); font-family: rooney-web, Arial, serif; font-size: 16px; white-space: normal; background-color: rgb(255, 255, 255);">从手持设备到桌面个人气溶胶监测仪，没有其他制造商有能力执行实时，直读气溶胶的监测与分析在任何环境或应用程序。</p><table><tbody><tr class="firstRow"><td width="470" valign="top"><p style="text-align:center"><img src="/zyhk/kbcms/ueditor/php/upload/image/20210716/1626399384141574.jpg" title="1626399384141574.jpg" alt="4146.jpg" width="216" height="164" style="width: 216px; height: 164px;"/></p></td><td width="470" valign="top"><img src="/zyhk/kbcms/ueditor/php/upload/image/20210716/1626399407135821.jpg" title="1626399407135821.jpg" alt="8530 8532(副本）.jpg" width="298" height="195" style="width: 298px; height: 195px;"/></td></tr><tr><td width="471" valign="top" style="word-break: break-all;" align="center">流量校准仪<br/></td><td width="470" valign="top" style="word-break: break-all;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 气溶胶监测仪</td></tr></tbody></table><p><br/></p>', '385', '', '67', '1626399823', '999', '0', '1', '', '', '0');
