INSERT INTO `tp_archives` VALUES ('577', '气体质量流量计', '型号：5300-5', 'TSI质量流量万用表5300-5提供双向流量测量，在宽流量范围内读数精度为2%。集成的温度和压力传感器补偿流量测量，以提供独立于温度或压力的质量流量读数。除了温度和绝对压力测量外，内置的累加器测量还允许用户以运行总量进行体积测量。', '0', '', '', '', '', '20210723\1057ca75f7ce7be29499cbe502f50e4a.jpg', '', '94', '', '368', '<p><img src="/zyhk/kbcms/ueditor/php/upload/image/20210723/1627021313808308.jpg" style="" title="1627021313808308.jpg"/></p><p><img src="/zyhk/kbcms/ueditor/php/upload/image/20210723/1627021313101021.jpg" style="" title="1627021313101021.jpg"/></p><p><br/></p>', '65', '1626969600', '991', '0', '1', '', '', '0');
