INSERT INTO `tp_test` VALUES ('428', '<p style="white-space: normal; line-height: 3em;"><strong><span style="font-size: 16px;">特性和优点</span></strong></p><p style="white-space: normal; line-height: 3em;"><span style="font-size: 16px;">使用4节C尺寸的碱电池</span></p><p style="white-space: normal; line-height: 3em;"><span style="font-size: 16px;">重量仅为3KG含（610 x 610(mm)）罩子</span></p><p style="white-space: normal; line-height: 3em;"><span style="font-size: 16px;">同时支持模拟显示和数字计数</span></p><p style="white-space: normal; line-height: 3em;"><span style="font-size: 16px;">可不使用罩子</span></p><p><br/></p>', '<p style="line-height: 2em;"><strong><span style="font-size: 16px;">测试原理和种类</span></strong></p><p style="line-height: 2em;"><span style="font-size: 16px;">在风量罩的设计中应用了不同的测试原理，适用于不同的测试条件。包括：热式、机械式和差压式。热式传感（TSI8371,ABT700,LOFLO6200）具有很好的灵敏度适合于较低风量。压差式风量罩（TSI8380）提供极高的分辨率和更宽的动态范围。从面板显示的种类又可分为模拟式和数字式。模拟式仪表（ABT700，LOFLO）由于非常直观仍然在工程 测试领域被使用。</span></p><p style="line-height: 2em;"><strong><span style="font-size: 16px;">&nbsp;</span></strong></p><p style="line-height: 2em;"><strong><span style="font-size: 16px;">风量罩测试中的注意事项</span></strong></p><p style="line-height: 2em;"><span style="font-size: 16px;">使用风量罩进行通风系统的平衡度测试的最大优点是直接读取风量，快速测量和操作简便。然而暖通空调系统（HVAC/R）的设计会影响测试的精度。需要考虑的因素包括：散流器或出风口的种类，上游气流的分布状况（弯管，风扇），管道阻力影响（管道面积变化），空气物理特性和风量罩的形状和尺寸。为提高测试的精度，可采用现场校准K因子用来对非标准的测试条件进行补偿。</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">&nbsp;</span></p><p style="line-height: 2em;"><strong><span style="font-size: 16px;">特性和优点</span></strong></p><p style="line-height: 2em;"><span style="font-size: 16px;">使用4节C尺寸的碱电池</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">重量仅为3KG含（610 x 610(mm)）罩子</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">同时支持模拟显示和数字计数</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">可不使用罩子</span></p><p><br/></p>', '<p style="line-height: 2em;"><strong><span style="font-size: 16px;">技术参数</span></strong></p><p style="line-height: 2em;"><span style="font-size: 16px;">流量范围：17-850m3/h(4.7-236L/S)</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">精度：3%全最程+8.5m3/h,(或2.4L/s)</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">重量：2.7Kg(含罩子)；2.1Kg(不含罩子)</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">&nbsp;</span></p><p style="line-height: 2em;"><strong><span style="font-size: 16px;">型号描述</span></strong></p><p style="line-height: 2em;"><span style="font-size: 16px;">6200型：含406mm x 406mm罩子（高度：200mm）</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">6200D型：含610mm x 610mm罩子</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">6200E型：不含罩子（基座直径：338mm）</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">6200F型：含406mm x 406mm罩子（高度：457mm）</span></p><p><br/></p>');
INSERT INTO `tp_test` VALUES ('431', '<p style="text-indent: 2em; line-height: 2em;"><span style="font-size: 16px;">&nbsp; 4140/4143/4040/4043/4045质量流量计适用于各种气体流动测量应用。无论是测量实验室还是制造车间的气流，TSI 的通用质量流量计都能提供准确的测量结果，并有多种数据输出选项。</span></p><p style="text-indent: 2em; line-height: 2em;"><span style="font-size: 16px;">&nbsp; 美国TSI4140/美国TSI4143/美国TSI4040/美国TSI4043/美国TSI4045质量流量计准确到读数的2%，而且拥有更大的量程。4ms 的快速响应时间，确保它能精确测量波动气流，尤其适合于闭环控制系统和集成于流量测量设备。此外，TSI质量流量计具有极低的压降，把管网背压及其对系统的影响降到最小</span></p><p><br/></p>', '<p><br/></p><table cellspacing="0" width="472"><tbody><tr class="firstRow"><td width="339" valign="center" style="padding: 0px; border-width: 1px; border-style: solid; border-color: initial;"><p><strong><span style="font-size: 16px;">美国TSI质量流量计产品特性</span></strong></p></td></tr><tr><td width="339" valign="center" style="padding: 0px; border-width: 1px; border-style: solid; border-color: initial;"><p><span style="font-size: 16px;">4ms&nbsp;快速响应</span></p></td></tr><tr><td width="339" valign="center" style="padding: 0px; border-width: 1px; border-style: solid; border-color: initial;"><p><span style="font-size: 16px;">准确到读数的± 2%</span></p></td></tr><tr><td width="339" valign="center" style="padding: 0px; border-width: 1px; border-style: solid; border-color: initial;"><p><span style="font-size: 16px;">高极限负荷比</span></p></td></tr><tr><td width="339" valign="center" style="padding: 0px; border-width: 1px; border-style: solid; border-color: initial;"><p><span style="font-size: 16px;">低压降</span></p></td></tr><tr><td width="339" valign="center" style="padding: 0px; border-width: 1px; border-style: solid; border-color: initial;"><p><span style="font-size: 16px;">流量的模拟信号输出</span></p></td></tr><tr><td width="339" valign="center" style="padding: 0px; border-width: 1px; border-style: solid; border-color: initial;"><p><span style="font-size: 16px;">流量、体积、压力、温度等的通用数字输出</span></p></td></tr><tr><td width="339" valign="center" style="padding: 0px; border-width: 1px; border-style: solid; border-colo