INSERT INTO `tp_test` VALUES ('434', '<p style="line-height: 2em; text-indent: 2em;"><span style="font-size: 16px;">&nbsp; 3076 恒流雾化气溶胶发生器 是一种科利森式喷雾器，它可生成 10<sup>7</sup>&nbsp;粒子/cm<sup>3</sup>浓度的恒定粒径的气溶胶。它的标称气溶胶流速为 3.0 升/分。不锈钢组件使其不仅适用于基础气溶胶研究，而且还适用于下列特定应用领域。</span></p><p><br/></p>', '<p style="line-height: 2em;"><strong><span style="font-size: 16px;">应用范围</span></strong></p><p style="line-height: 2em;"><span style="font-size: 16px;">·&nbsp;过滤器测试</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">·&nbsp;仪器校准</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">·&nbsp;材料合成</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">·&nbsp;生物和医学研究</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">·&nbsp;需要多分散高浓度气溶胶的研究</span></p><p style="line-height: 2em;"><span style="font-size: 16px;"><br/></span></p><p style="line-height: 2em;"><strong><span style="font-size: 16px;">特点</span></strong></p><p style="line-height: 2em;"><span style="font-size: 16px;">107&nbsp;粒子/cm3浓度</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">3.0 L/min 的标称气溶胶流速</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">例子气溶胶：</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">多达2µm PSL</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">模式在20至100纳米范围内的盐雾</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">石油气溶胶</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">&nbsp;</span></p><p><br/></p>', '');
INSERT INTO `tp_test` VALUES ('440', '<p><strong><span style="font-size: 16px;">移动APP，功能组件，风速计和测量探头</span></strong></p><p><span style="font-size: 16px;">AirPro风速计是坚固紧湊、易于设置的高精度测量仪器，由风速计（AP500），移动应用软件，用户自定义测量探头（VT-S,VT-A,VTH-S,VTH-A和TH-S）以及功能组件套装（基础版，高级版，专业版）组成。</span></p><p><span style="font-size: 16px;">仪器通过可靠的温湿度计或热式风速&nbsp;计技术进行测量，测量结果显示于风速计显示屏上，同时可通过蓝牙传输到您装有AirPro移动应用软件的移动Android或IOS手持设备中。基于使用偏好和登陆等级，用户能够通过移动应用软件远程激活仪器的相应功能。</span></p><p><br/></p>', '<p><strong><span style="font-size: 16px;">应用</span></strong></p><p><span style="font-size: 16px;">HVAC测试和平衡</span></p><p><span style="font-size: 16px;">洁净室测试</span></p><p><span style="font-size: 16px;">生物安全柜和实验室通风柜测试</span></p><p><span style="font-size: 16px;">HVAC调试与故障排查</span></p><p><span style="font-size: 16px;">通风评估</span></p><p><span style="font-size: 16px;">表面风速测量</span></p><p><span style="font-size: 16px;">特性和优势</span></p><p><span style="font-size: 16px;">多种即插即用探头配置</span></p><p><span style="font-size: 16px;">可通过层叠组合式探头扩展测量</span></p><p><span style="font-size: 16px;">高对度显示屏，在昏暗光线条件下仍能清晰显示</span></p><p><span style="font-size: 16px;">高精度风速测试</span></p><p><span style="font-size: 16px;">长寿命可充电锂电池，降低用户的购置成本</span></p><p><span style="font-size: 16px;">包含校准证书</span></p><p><span style="font-size: 16px;">真正的单手操作</span></p><p><br/><strong><span style="font-size: 16px;">基础功能版</span></strong></p><p><span style="font-size: 16px;">可通过应用商店和Google Play免费下载</span></p><p><span style="font-size: 16px;">自动连接仪器</span></p><p><span style="font-size: 16px;">远程显示仪器测量结果</span></p><p><span style="font-size: 16px;">设置目标值</span></p><p><span style="font-size: 16px;">同时查看多个测量结果</span></p><p><span style="font-size: 16px;">支持多种语言</span></p><p><span style="font-size: 16px;">以英制或公制单位显示测量结果</span></p><p><br/><strong><span style="font-size: 16px;">高级功能版套装—在基础全部功能的基础上还具有：</span></strong></p><p><span style="font-size: 16px;">可定制的功能套装</span></p><p><span style="font-size: 16px;">同时连接多达两台仪器，能够实时显示和记录数据</span></p><p><span style="font-size: 16px;">能够根据用户输入的管道尺寸进行风量计算</span></p><p><span style="font-size: 16px;">计算湿球和露点温度（取决于探头）</span></p><p><span style="font-size: 16px;">将测量结果记录到智成设备中</span></p><p><span style="font-size: 16px;">输出包含评论和图片的数据用于生成报告</span></p><p><br/>专业功能版套装—在基础版和高级版全部功能的基础上还具有：</p><p><span style="font-size: 16px;">可定制的功能套装</span></p><p><span style="font-size: 16px;">同时连接多达六台仪器，能够实时显示和记录数据</span></p><p><span style="font-size: 16px;">内置的风管截面法测量流程同时支持等面积和Log-Tchebycheff管道风量测量方法</span></p><p><span style="font-size: 16px;">能够通过SD卡为用户的长期或短期无人值守数据记录应用提供内部存储空间</span></p><p><br/></p>', '<p><strong><span style="font-size: 16px;">技术参数</span></strong></p><p><br/></p><p><strong><span style="font-size: 16px;">AP500，VT-S,VT-A,VTH-S,VTH-S,VTH-A以及TH-S</span></strong></p><p><strong><span style="font-size: 16px;">AP500型</span></strong></p><p><strong><span style="font-size: 16px;">大气压</span></strong></p><p><span style="font-size: 16px;">范围：20.36-36.65in.汞柱（517.1-930.9mm汞柱）</span></p><p><span style="font-size: 16px;">精度：读数的±2%</span></p><p><span style="font-size: 16px;">仪器温度范围</span></p><p><span style="font-size: 16px;">操作（电子）：40-113℉（5-45℃）</span></p><p><span style="font-size: 16px;">储存：-4-140℉（-20-60℃）</span></p><p><span style="font-size: 16px;">显示屏</span></p><p><span style="font-size: 16px;">有机发光二级管（OLED）0.4in.(10mm)数字高度</span></p><p><span style="font-size: 16px;">外部尺寸</span></p><p><span style="font-size: 16px;">2.1in. x 8.5in. x 1.6in.(53mm x 181mm x 40mm)</span></p><p><span style="font-size: 16px;">重量（含电池）</span></p><p><span style="font-size: 16px;">0.5lbs.(0.23Kg)</span></p><p><span style="font-size: 16px;">电源要求</span></p><p><span style="font-size: 16px;">AirPro锂电池 3500mAh</span></p><p><span style="font-size: 16px;">AC电源适配器（TSI零件号800531）</span></p><p><span style="font-size: 16px;">输入：90-240VAC,50-60Hz</span></p><p><span style="font-size: 16px;">输出：5VDC,2A</span></p><p><span style="font-size: 16px;">电池续航时间</span></p><p><span style="font-size: 16px;">100ft/min(0.5m/s)和77℉（25℃）条件下续航8小时以上</span></p><p><span style="font-size: 16px;">AIRPRO测量探头</span></p><p><span style="font-size: 16px;">风速（VT-S,VT-A,VTH-S,VTH-A）</span></p><p><span style="font-size: 16px;">范围：0-6000ft/min(0-30m/s)</span></p><p><span style="font-size: 16px;">精度：读数的±3%或±3ft/min(±0.015m/s),取较大者</span></p><p><span style="font-size: 16px;">分辨率：1ft/min(0.01m/s)</span></p><p><span style="font-size: 16px;">温度（VT-S,VT-A,VTH-S,VTH-A,TH-S）</span></p><p><span style="font-size: 16px;">范围：14-150℉（-10-65℃）</span></p><p><span style="font-size: 16px;">精度：±0.5℉（±0.3℃）</span></p><p><span style="font-size: 16px;">相对湿度（VTH-S,VTH-A,TH-S）</span></p><p><span style="font-size: 16px;">范围：5-95%RH</span></p><p><span style="font-size: 16px;">精度：±3%RH</span></p><p><span style="font-size: 16px;">范围：0.1%RH</span></p><p><span style="font-size: 16px;">直杆探头尺寸（VT-S,VTH-S,TH-S）</span></p><p><span style="font-size: 16px;">探头长度：12in.(305mm)</span></p><p><span style="font-size: 16px;">探头直径（最大）：0.375in.(9.5mm)</span></p><p><span style="font-size: 16px;">铰接探头尺寸（VT-A,VTH-A）</span></p><p><span style="font-size: 16px;">探头长度：12in.(305mm)</span></p><p><span style="font-size: 16px;">探头直径（最大）：0.375in.(9.5mm)</span></p><p><span style="font-size: 16px;">铰接部分长度：6.0in.(15.24cm)</span></p><p><span style="font-size: 16px;">探头加长杆尺寸（800529）</span></p><p><span style="font-size: 16px;">探头长度：12in.(305mm)</span></p><p><span style="font-size: 16px;">探头直径（最大）：0.375in.(9.5mm)</span></p><p><br/></p>');
INSERT INTO `tp_test` VALUES ('441', '<p style="margin-top: 0px; margin-bottom: 0px; line-height: 22px; background: rgb(255, 255, 255); text-indent: 2em;"><span style="font-family: 微软雅黑; color: rgb(51, 51, 51); font-size: 16px;">美国TSI9535&nbsp;和美国TSI9545&nbsp;型风速表（风速仪）是具有一台普通仪表价位的多功能仪表。该风速表（风速仪）可以使用一个具有多个传感器的探头同时测量和记录通空气中的多个参数。这两个型号的仪器都可以测量风速、温度和计算风量。美国TSI9545&nbsp;型还可以测量相对湿度，并计算出露点温度和湿球温度。美国TSI9535&nbsp;和美国TSI9535-A&nbsp;型具有可伸缩的直探头。美国TSI9545&nbsp;和美国TSI9545A具有可伸缩、可折弯的铰接式探头。</span></p><p><br/></p>', '<p style="line-height: 2em;"><strong><span style="font-size: 16px;">功能和特点:</span></strong></p><p style="line-height: 2em;"><span style="font-size: 16px;">操作简便</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">精密的风速测量</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">同时测量温度和风速</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">最多能同时显示三个测量参数</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">可测量湿度(9545&nbsp;和9545A)</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">可计算体积流量，在实际/&nbsp;标准状况风速之间切换</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">包括LogDat2TM&nbsp;数据下载软件探头可以折弯&nbsp;(9535A&nbsp;和9545A)</span></p><p><br/></p>', '<p style="text-align: center;"><br/></p><table cellspacing="0" style="width:900px;margin:0 auto"><tbody><tr class="firstRow"><td width="186" valign="center" colspan="2" style="padding: 0px; border-width: 1px; border-style: outset; border-color: windowtext;"><p style="text-align: center;"><span style="font-size: 16px;">检测参数/ 型号</span></p></td><td width="68" valign="center" style="padding: 0px; border-left: none; border-right: 1px outset windowtext; border-top: 1px outset windowtext; border-bottom: 1px outset windowtext;"><p style="text-align: center;"><span style="font-size: 16px;">9535</span></p></td><td width="76" valign="center" style="padding: 0px; border-left: none; border-right: 1px outset windowtext; border-top: 1px outset windowtext