INSERT INTO `tp_test` VALUES ('547', '<ul class=" list-paddingleft-2" style="list-style-type: disc;"><li><p style="text-align: left; background: white; line-height: 3em;"><span style="font-family: 宋体; font-size: 14px;">温度和相对湿度测量帮助确定热舒适度<br/></span></p></li><li><p style="white-space: normal; line-height: 3em;"><span style="font-size: 14px;"><span style="font-family: 宋体;">通过CO<sub>2</sub></span><span style="font-family: 宋体;">&nbsp;</span><span style="font-family: 宋体;">或温度计算室外空气的%</span></span></p></li><li><p style="white-space: normal; line-height: 3em;"><span style="font-family: 宋体; font-size: 14px;">直接计算露点温度和湿球温度</span></p></li><li><p style="white-space: normal; line-height: 3em;"><span style="font-family: 宋体; font-size: 14px;">电化学传感器测量CO（7545）</span></p></li><li><p style="white-space: normal; line-height: 3em;"><span style="font-family: 宋体; font-size: 14px;">7525和7545可最大显示3个参数</span></p></li><li><p style="white-space: normal; line-height: 3em;"><span style="font-family: 宋体; font-size: 14px;">7515和7535仅能显示1个参数</span></p></li></ul><p style="text-align: left; background: white; line-height: 1.75em;"><span style="font-family: 宋体; color: black; font-size: 14px;"><br/></span></p><p><br/></p>', '<p style="text-align: left; background: white; line-height: 1.75em;"><span style="font-size: 14px;"><strong><span style="font-family: 宋体; color: rgb(0, 51, 153);">室内空气品质仪</span></strong><strong><span style="font-family: 宋体; color: rgb(0, 51, 153);"></span></strong></span></p><p style="text-align: left; background: white; line-height: 1.75em;"><span style="font-size: 14px;"><strong><span style="font-size: 14px; font-family: 宋体; color: rgb(0, 51, 153);">型号：7515；7525；7535；7545</span></strong><strong><span style="font-size: 14px; font-family: 宋体; color: rgb(0, 51, 153);"></span></strong></span></p><p style="text-align: left; background: white; line-height: 1.75em;"><span style="font-family: 宋体; color: black; font-size: 14px;">美国TSI的IAQ-CALC<sup>TM</sup>测试仪是用于室内空气品质研究和监测的出色的仪器。美国TSI7515室内品质测试仪是测试CO<sub>2</sub>的低价位仪器。美国TSI7535增加了自动存储CO<sub>2</sub>数据功能。美国TSI7525和美国TSI7545可以同时测量和存储多个参数。美国TSI7525可以测量CO<sub>2</sub>、温度、湿度，并计算露点温度、湿球温度和室外空气的相对湿度。美国TSI7545还可以测量CO。</span></p><p style="text-align: left; background: white; line-height: 1.75em;"><span style="font-family: 微软雅黑, sans-serif; color: rgb(51, 51, 51); font-size: 14px;">&nbsp;</span></p><p style="text-align: left; line-height: 1.75em;"><span style="font-size: 14px;"><strong><span style="font-size: 14px; font-family: 宋体; color: rgb(0, 51, 153);">特点和优点（所有型号）</span></strong><strong><span style="font-size: 14px; font-family: 宋体; color: rgb(0, 51, 153);"></span></strong></span></p><p style="text-align: left; line-height: 1.75em;"><span style="font-size: 14px;"><span style="font-size: 12px; font-family: 宋体; color: black; background: white;">●</span><span style="font-size: 12px; font-family: 宋体; color: black;">&nbsp;</span><span style="font-size: 12px; font-family: 宋体; color: black; background: white;">可以稳定、精确测量的低漂移NDIR的CO<sub>2</sub>传感器</span></span></p><p style="text-align: left; line-height: 1.75em;"><span style="font-size: 14px;"><span style="font-size: 12px; font-family: 宋体; color: black; background: white;">●</span><span style="font-size: 12px; font-family: 宋体; color: black;">&nbsp;</span><span style="font-size: 12px; font-family: 宋体; color: black; background: white;">采样功能可以记录多点测量数据</span></span></p><p style="text-align: left; line-height: 1.75em;"><span style="font-size: 14px;"><span style="font-size: 12px; font-family: 宋体; color: black; background: white;">●</span><span style="font-size: 12px; font-family: 宋体; color: black;">&nbsp;</span><span style="font-size: 12px; font-family: 宋体; color: black; background: white;">人体工程学外形设计</span></span></p><p style="text-align: left; line-height: 1.75em;"><span style="font-size: 14px;"><strong><span style="font-size: 12px; font-family: 宋体; color: rgb(0, 51, 153);"><br/></span></strong><strong><span style="font-size: 12px; font-family: 宋体; color: rgb(0, 51, 153);"></span></strong></span></p><p style="text-align: left; line-height: 1.75em;"><span style="font-size: 14px;"><strong><span style="font-size: 14px; font-family: 宋体; color: rgb(0, 51, 153);">7525</span></strong><strong><span style="font-size: 14px; font-family: 宋体; color: rgb(0, 51, 153);">&nbsp;</span></strong><strong><span style="font-size: 14px; font-family: 宋体; color: rgb(0, 51, 153);">和7545</span></strong><strong><span style="font-size: 14px; font-family: 宋体; color: rgb(0, 51, 153);">&nbsp;</span></strong><strong><span style="font-size: 14px; font-family: 宋体; color: rgb(0, 51, 153);">型</span></strong><strong><span style="font-size: 14px; font-family: 宋体; color: rgb(0, 51, 153);"></span></strong></span></p><p style="text-align: left; line-height: 1.75em;"><span style="font-family: 宋体; color: black; font-size: 14px;">温度和相对湿度测量帮助确定热舒适度</span></p><p style="text-align: left; line-height: 1.75em;"><span style="font-size: 14px;"><span style="font-size: 12px; font-family: 宋体; color: black;">通过CO<sub>2</sub></span><span style="font-size: 10px; font-family: 宋体; color: black;">&nbsp;</span><span style="font-size: 12px; font-family: 宋体; color: black;">或温度计算室外空气的%</span></span></p><p style="text-align: left; line-height: 1.75em;"><span style="font-family: 宋体; color: black; font-size: 14px;">直接计算露点温度和湿球温度</span></p><p style="text-align: left; line-height: 1.75em;"><span style="font-family: 宋体; color: black; font-size: 14px;">电化学传感器测量CO（7545）</span></p><p style="text-align: left; line-height: 1.75em;"><span style="font-family: 宋体; color: black; font-size: 14px;">7525和7545可最大显示3个参数</span></p><p style="text-align: left; line-height: 1.75em;"><span style="font-family: 宋体; color: black; font-size: 14px;">7515和7535仅能显示1个参数</span></p><p style="text-align: left; line-height: 1.75em;"><span style="font-size: 14px;"><strong><span style="font-size: 12px; font-family: 宋体; color: rgb(0, 51, 153);"><br/></span></strong><strong><span style="font-size: 12px; font-family: 宋体; color: rgb(0, 51, 153);"></span></strong></span></p><p style="text-align: left; line-height: 1.75em;"><span style="font-size: 14px;"><strong><span style="font-size: 14px; font-family: 宋体; color: rgb(0, 51, 153);">应用范围</span></strong><strong><span style="font-size: 14px; font-family: 宋体; color: rgb(0, 51, 153);"></span></strong></span></p><p style="text-align: left; line-height: 1.75em;"><span style="font-family: 宋体; color: black; font-size: 14px;">验证建筑里HVAC系统运行情况</span></p><p style="text-align: left; line-height: 1.75em;"><span style="font-family: 宋体; color: black; font-size: 14px;">检查建筑内室内空气品质，以提高人员工作效率</span></p><p style="text-align: left; line-height: 1.75em;"><span style="font-family: 宋体; color: black; font-size: 14px;">符合标准规范的要求</span></p><p style="text-align: left; line-height: 1.75em;"><span style="font-family: 宋体; color: black; font-size: 14px;">&nbsp;</span></p><p style="text-align: left; line-height: 1.75em;"><span style="font-size: 14px;"><strong><span style="font-size: 14px; font-family: 宋体; color: rgb(0, 51, 153);">7515</span></strong><strong><span style="font-size: 14px; font-family: 宋体; color: rgb(0, 51, 153);">、7525、7535</span></strong><strong><span style="font-size: 14px; font-family: 宋体; color: rgb(0, 51, 153);">&nbsp;</span></strong><strong><span style="font-size: 14px; font-family: 宋体; color: rgb(0, 51, 153);">和7545</span></strong><strong><span style="font-size: 14px; font-family: 宋体; color: rgb(0, 51, 153);">&nbsp;</span></strong><strong><span style="font-size: 14px; font-family: 宋体; color: rgb(0, 51, 153);">型的数据记录功能</span></strong><strong><span style="font-size: 14px; font-family: 宋体; color: rgb(0, 51, 153);"></span></strong></span></p><p style="text-align: left; line-height: 1.75em;"><span style="font-family: 宋体; color: black; font-size: 14px;">同时显示所有测量的参数，并记录带时间和日期标识的数据</span></p><p style="text-align: left; line-height: 1.75em;"><span style="font-size: 14px;"><span style="font-size: 12px; font-family: 宋体; color: black;">TSI LogDat2<sup>TM</sup></span><span style="font-size: 10px; font-family: 宋体; color: black;">&nbsp;</span><span style="font-size: 12px; font-family: 宋体; color: black;">软件使与电脑的数据传输更加便捷</span></span></p><p style="text-align: left; line-height: 1.75em;"><span style="font-family: 宋体; color: black; font-size: 14px;">可以在仪器的屏幕上浏览数据，或下载到电脑上生成报告</span></p><p style="text-align: left; line-height: 1.75em;"><span style="font-family: 宋体; color: black; font-size: 14px;">统计数据功能可以显示平均值、最大值、最小值和采样数据点的个数</span></p><p><br/></p>', '<table cellspacing="0" cellpadding="0" width="968"><tbody><tr class="firstRow"><td colspan="6" style="padding: 0px; border-width: 1px; border-style: solid; border-color: rgb(0, 0, 0);"><p style="line-height: 22px"><span style="font-family: 微软雅黑, sans-serif; color: rgb(51, 51, 51); font-size: 14px;">技术参数</span></p></td></tr><tr><td style="padding: 0px; border-width: 1px; border-style: solid; border-color: rgb(0, 0, 0);" width="150"><br/></td><td style="padding: 0px; border-width: 1px; border-style: solid; border-color: rgb(0, 0, 0);" width="176"><br/></td><td style="padding: 0px; border-width: 1px; border-style: solid; border-color: rgb(0, 0, 0);" width="128"><p style="line-height:22px"><span style="font-family: 微软雅黑, sans-serif; color: rgb(51, 51, 51); font-size: 14px;">7515</span></p></td><td style="padding: 0px; border-width: 1px; border-style: solid; border-color: rgb(0, 0, 0);" width="264"><p style="line-height:22px"><span style="font-family: 微软雅黑, sans-serif; color: rgb(51, 51, 51); font-size: 14px;">7525</span></p></td><td style="padding: 0px; border-width: 1px; border-style: solid; border-color: rgb(0, 0, 0);" width="130"><p style="line-height:22px"><span style="font-family: 微软雅黑, sans-serif; color: rgb(51, 51, 51); font-size: 14px;">7545</span></p></td><td style="padding: 0px; border-width: 1px; border-style: solid; border-color: rgb(0, 0, 0);" width="119"><p style="line-height:22px"><span style="font-family: 微软雅黑, sans-serif; color: rgb(51, 51, 51); font-size: 14px;">7535</span></p></