INSERT INTO `tp_test` VALUES ('474', '<p dir="ltr" style="line-height: 2em;"><strong><span style="font-size: 16px;">Model：3330</span></strong></p><p style="line-height: 2em;"><strong><span style="font-size: 16px;">应用领域：</span></strong></p><ul class=" list-paddingleft-2" style="list-style-type: disc;"><li><p style="line-height: 2em;"><span style="font-size: 16px;">过滤测试（ASHRAE52.2）</span></p></li><li><p style="line-height: 2em;"><span style="font-size: 16px;">室内空气质量</span></p></li><li><p style="line-height: 2em;"><span style="font-size: 16px;">工作场所监测</span></p></li><li><p style="line-height: 2em;"><span style="font-size: 16px;">室外环境监测</span></p></li><li><p style="line-height: 2em;"><span style="font-size: 16px;">工业测量</span></p></li><li><p style="line-height: 2em;"><span style="font-size: 16px;">源排放监测和控制</span></p></li></ul><p><br/><br/></p><p><br/></p>', '<p style="line-height: 2em;"><span style="font-size: 16px;">光学颗粒物粒径谱仪</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">Model：3330</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">&nbsp; &nbsp; &nbsp;tsi3330型光学颗粒物粒径谱仪简单轻便，能够对颗粒物浓度和粒径谱分布进行快速和准确的测量。基于TSI40年气溶胶仪器设计的经验，本款产品使用120度光散射角收集散射光强度和精密的电子处理系统，从而得到高质量和高精度的数据。同时，TSI工厂严格的标定标准也确保仪器的精确性。TSI3330不仅可以单独使用，而且还可以放入TSI的外场环境箱中在野外使用。</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">&nbsp;</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">应用</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">&nbsp;光学仪器由于其易于操作、测量速度快、结实耐用，而被广泛应用于不同领域。</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">&nbsp;一些主要的应用如下：</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">过滤测试（ASHRAE52.2）</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">室内空气质量</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">工作场所监测</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">室外环境监测</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">工业测量</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">源排放监测和控制</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">&nbsp;</span></p><p style="line-height: 2em;"><strong><span style="font-size: 16px;">特性和优点</span></strong></p><p style="line-height: 2em;"><span style="font-size: 16px;">0.5微米时粒径分辨率小于5%</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">粒径通道用户可调</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">检测粒径范围：0.3—10微米，最多16通道</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">检测浓度范围：0-3000个/cm3</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">彩色触摸屏，直观的用户界面</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">完全满足ISO 21501-01/04要求</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">输入折射率和密度可以同时显示颗粒物数浓度和质量浓度</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">收集的采样膜可进行称重测量和化学分析</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">电池电量最大可供20小时的操作</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">最多可存储30000个数据</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">记录每个样品的温度和压力</span></p><p><br/></p>', '<p><br/></p><table cellspacing="0" cellpadding="0" width="968"><tbody><tr class="firstRow"><td valign="top" style="padding: 0px 7px; border-color: rgb(0, 0, 0); border-width: 1px; border-style: solid;" width="155"><p style="text-align: center;"><span style="font-size: 16px;">型号</span></p></td><td colspan="2" valign="top" style="padding: 0px 7px; border-color: rgb(0, 0, 0); border-width: 1px; border-style: solid;"><p style="text-align: center;"><span style="font-size: 16px;">3330</span></p></td></tr><tr><td valign="top" style="padding: 0px 7px; border-width: 1px; border-style: solid; border-color: rgb(0, 0, 0);" width="155"><p style="text-align: center;"><span style="font-size: 16px;">测量原则</span></p></td><td colspan="2" valign="