INSERT INTO `tp_test` VALUES ('494', '<p><strong><span style="font-size: 16px;">AIRPRO微压计</span></strong></p><p><strong><span style="font-size: 16px;">移动APP，功能组件，微压计</span></strong></p><p><span style="font-size: 16px;">AirPro微压计是坚固紧凑、易于设置的高精度测量仪器，由微压计（AP800）,移动应用软件以及功能组件套装（基础版，高级版，专业版）组成。</span></p><p><span style="font-size: 16px;">AirPro AP800型微压计能够测量静压和压差，而且能够通过蓝牙低能耗（BLE）技术和智成设备实现无缝连接。仪器能够在显示屏显示测量数据的同时将数据无线实时传输到AirPro移动应用软件上——您可以在Android或IOS移动手持设备上运行该软件。基于使用偏好和登陆等级，用户能够通过移动应用软件远程激活仪器的相应功能。</span></p><p><br/></p><p><br/></p>', '<p><span style="font-size: 16px;"><strong>应用</strong></span></p><p><span style="font-size: 16px;">HVAC调试与故障排查</span></p><p><span style="font-size: 16px;">测试和平衡</span></p><p><span style="font-size: 16px;">皮托管测量风管内风速</span></p><p><span style="font-size: 16px;">静压测量</span></p><p><span style="font-size: 16px;">压差测量</span></p><p><span style="font-size: 16px;">特性和优势</span></p><p><span style="font-size: 16px;">使用皮托管测量时可计算风速</span></p><p><span style="font-size: 16px;">在仪器启动时自动调零压力传感器</span></p><p><span style="font-size: 16px;">带挂钩的压力接口，能够牢牢固定采样管</span></p><p><span style="font-size: 16px;">高对比度显示屏，在昏暗光线条件下仍能清晰显示</span></p><p><span style="font-size: 16px;">压差和静压测量范围-15—+15in.H2O(-3735—+3735Pa)</span></p><p><span style="font-size: 16px;">长寿命可充电锂电池，降低用户的购置成本</span></p><p><span style="font-size: 16px;">包含校准证书</span></p><p><br/><span style="font-size: 16px;"><strong>基础功能版</strong></span></p><p><span style="font-size: 16px;">可通过应用商店和Google Play免费下载</span></p><p><span style="font-size: 16px;">自动连接仪器</span></p><p><span style="font-size: 16px;">远程显示仪器测量结果</span></p><p><span style="font-size: 16px;">设置目标值</span></p><p><span style="font-size: 16px;">同时查看多个测量结果</span></p><p><span style="font-size: 16px;">支持多种语言</span></p><p><span style="font-size: 16px;">以英制或公制单位显示测量结果</span></p><p><span style="font-size: 16px;">高级功能套装—在基础版全部功能的基础上还具有：</span></p><p><span style="font-size: 16px;">可定制的功能套装</span></p><p><span style="font-size: 16px;">同时连接多达两台仪器，能够实时显示和记录数据</span></p><p><span style="font-size: 16px;">能够根据用户输入的管道尺寸或K因数进行风量计算</span></p><p><span style="font-size: 16px;">计算湿球和露点温度（取决于探头）</span></p><p><span style="font-size: 16px;">将测量结果记录到智能设备中</span></p><p><span style="font-size: 16px;">输出包含评论和图片的数据用于生成报告</span></p><p><br/><span style="font-size: 16px;"><strong>专业功能版套装—在基础版和高级版全部功能的基础上还具有：</strong></span></p><p><span style="font-size: 16px;">可定制的功能套装</span></p><p><span style="font-size: 16px;">同时连接多达六台仪器，能够实时显示和记录数据</span></p><p><span style="font-size: 16px;">内置的风管截面法测量流程同时支持等面积和Log－Techebycheff管道风量测量方法</span></p><p><span style="font-size: 16px;">能够通过SD卡为用户的长期或短期无人值守数据记录应用提供应用提供内部存储空间</span></p><p><br/></p>', '<p style="line-height: 2em;"><strong><span style="font-size: 16px;">规格参数</span></strong></p><p style="line-height: 2em;"><span style="font-size: 16px;">AP800型及附件</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">静压/压差</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">范围：-15-+15in.H2O(-28.0—+28.0mm汞柱，-3735—+3735Pa)</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">精度：计数±1%&nbsp;±0.005in.H2O(±0.01mm汞柱，±1Pa)</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">分辨率：0.001in.H2O(0.1Pa，0.01mm汞柱)</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">风速（皮托管）</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">范围：250—15500 ft/min(1.27-78.7 m/s)</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">精度：2000 ft/min(10.16 m/s)下±1.5%</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">分辨率：1ft/min(0.1m/s)</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">大气压（AP800）</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">范围：20.36—36.6in.汞柱（517.1—930.9mm汞柱）</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">精度：计数的±2%</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">仪器温度范围</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">操作：40—113℉（5-45℃）</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">储存：-4—140℉（-20—60℃）</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">显示屏</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">有机发光二级管（OLED）0.4in.(10mm)数字高度</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">外部尺寸</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">2.1in.x 7.1in x 1.6in.(53mm x 181mm x 40mm)</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">主机重量</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">含电池重量：0.45lbs(0.20Kg)</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">电源要求</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">AirPro锂电池&nbsp;3500mAh</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">AC电源适配器（TSI零件号800531）</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">输入：90—240VAC,50—60Hz</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">输出：5VDC,2A</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">电池续航时间</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">32小时以上</span></p><p><br/></p>');
INSERT INTO `tp_test` VALUES ('495', '<p style="text-indent: 2em;"><span style="font-size: 16px;">正负压管道漏风量测试系统（ PANDA ）为承包商、调试工程师和研究开发技术人员提供测试设备的最佳选择，用于定量检测管道和其它区域的空气泄露，以及测试管道系统性能。 PANDA 提供一种快速、准确的自动化解决方案，确保符合 EN12237 ， EN1507 ， EN13779 ， EUROVENT2/2 和 SMACNA 标准，提高建筑节能。</span></p><p><br/></p>', '<p><strong><span style="font-size: 16px;">美国TSIPAN341特性和优点&nbsp;</span></strong></p><p><span style="font-size: 16px;">集成在一套系统上的正负压管道泄露测试。&nbsp;</span></p><p><span style="font-size: 16px;">&nbsp;通过测试和尽量减少管道泄露达到节能的目的。&nbsp;</span></p><p><span style="font-size: 16px;">&nbsp;符合以下标准：&nbsp;</span></p><p><span style="font-size: 16px;">EN13779 建筑物通风 —— 达到良好空气质量可接受的安装和运行成本&nbsp;</span></p><p><span style="font-size: 16px;">EN12237 建筑物通风 —— 管道系统中环形薄板金属管道的强度和泄露&nbsp;</span></p><p><span style="font-size: 16px;">EN12237 建筑物通风 —— 测量方法和测试程序&nbsp;</span></p><p><span style="font-size: 16px;">HVCA DW / 143 建筑物通风——通风管道泄漏测试指导文件&nbsp;</span></p><p><span style="font-size: 16px;">EUROVENT2/2 薄板金属通风管道分散系统的空气泄漏率&nbsp;</span></p><p><span style="font-size: 16px;">SMACNA 美国金属散热和空调承包商协会&nbsp;</span></p><p><span style="font-size: 16px;">精度为流量的 ±2.5% &nbsp;</span></p><p><span style="font-size: 16px;">独特性能并可进行转速控制的风机能够在短时间内完成对管路系统静压的测试。&nbsp;</span></p><p><span style="font-size: 16px;">适合放在卡车和旅行车的后备箱中。&nbsp;</span></p><p><span style="font-size: 16px;">PAN341 标配有 TA465-P-NB多功能通风表和 PVM610 微压计。&nbsp;</span></p><p><span style="font-size: 16px;">实时自动计算泄漏率&nbsp;</span></p><p><span style="font-size: 16px;">同时显示流量泄漏率和静压&nbsp;&nbsp;</span></p><p><span style="font-size: 16px;">对于给定的密封等级能够提供一个通过 / 失败的结果显示&nbsp;</span></p><p><span style="font-size: 16px;">自动将实况体积流量泄漏率校正为标准温度和压力（ STP ）&nbsp;</span></p><p><span style="font-size: 16px;">实时监测大气压和温度&nbsp;</span></p><p><span style="font-size: 16px;">存储的数据能够下载生成报告和文件&nbsp;</span></p><p><span style="font-size: 16px;">与 8934 型便携式打印机配合使用&nbsp;</span></p><p><span style="font-size: 16px;">携带重量 45 公斤（ 99 磅）。</span></p><p><br/></p>', '<p style="line-height: 2em;"><strong><span style="font-size: 16px;">压力测量（PVM610）</span></strong></p><p style="line-height: 2em;"><span style="font-size: 16px;">&nbsp;&nbsp;&nbsp;量程：±3，735 Pa （±15in.W.G.）</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">&nbsp;&nbsp; 分辨率：0.1Pa （0.001 in.W.G.）</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">&nbsp;&nbsp; 准确度：读数的1%+1 Pa （+0.005&nbsp; in.W.G.）</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">&nbsp;&nbsp; 管道静压量程：±2500Pa&nbsp; （±10&nbsp; In.W.G.）</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">&nbsp;&nbsp;&nbsp;风量测量（TA465-P-NB）</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">&nbsp;&nbsp; Wilson辐射流量探架&nbsp;&nbsp;&nbsp;&nbsp;高泄露量程：10-200l/s （36-720m3/hr,21-424cfm）</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">&nbsp;&nbsp;&nbsp;15mm锥形喷嘴&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;低泄露量程：1-13 l/s （3.6-46.9m3/hr,2-27.5cfm）</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">&nbsp;&nbsp; 分辨率&nbsp;：&nbsp; 0.01 l/s （0.01m3/hr,0.01cfm）</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">&nbsp;&nbsp;&nbsp;准确度&nbsp;：&nbsp;&nbsp;读数的±2.5%±0.01&nbsp;l/s （±0.04 m3/hr,±0.02cfm）</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">&nbsp;&nbsp; 温度测量（TA450-P-NB）</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">&nbsp;&nbsp; K型热电偶&nbsp;：EN60584（IEC&nbsp; 584）</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">&nbsp;&nbsp; 大气压测试（TA465-P-NB）</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">&nbsp;&nbsp; 量程&nbsp;：&nbsp;690 to 1241hPa（517.5 to 930.87mmHg,20.36 to 36.648 in.Hg）</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">&nbsp;&nbsp; 准确度： 读数的±2%</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">&nbsp;&nbsp;&nbsp;电源要求：220-240V，单相，50/60Hz,10A</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">&nbsp;&nbsp; 仪器尺寸：1130mm×660mm×660mm</span></p><p><br/></p>');
INSERT INTO `tp_test` VALUES ('507', '<p style="text-indent: 2em;"><span style="font-size: 16px;">&nbsp; 美国TSI 风速传感器使用热风力测定技术来测量风速。三种不同类型的传感器为您的精确测量提供了灵活性和广泛的动态范围。持久耐用的美国TSI8455一般检测探头和美国TSI8465 无窗检测探头在诸多不同的应用领域中均表现出了高精度和快速响应时间。而美国TSI8475全方位探管则可以在低风速情况下进行精确测量并可以理想地应用于多种不同风向的情况。所有这些型号都可以由用户设置风速范围和输出选项。</span></p><p><br/></p>', '<p><br/></p><table cellspacing="0" style="width:700px;margin:0 auto" width="-21"><tbody><tr class="firstRow"><td width="209" valign="center" style="padding: 0px; border-width: 1px; border-style: solid; border-color: initial; background: rgb(255, 255, 255);"><p style="text-align: center;"><span style="font-size: 16px;">一般检测</span></p></td><td width="204" valign="center" style="padding: 0px; border-width: 1px; border-style: solid; border-color: initial; background: rgb(255, 255, 255);"><p style="text-align: center;"><span style="font-size: 16px;">无窗检测</span></p></td><td width="214" valign="center" style="padding: 0px; border-width: 1px; border-style: solid; border-color: initial; background: rgb(255, 255, 255);"><p style="text-align: center;"><span style="font-size: 16px;">全方位检测</span></p></td></tr><tr><td valign="center" style="padding: 0px; border-width: 1px; border-style: solid; border-color: initial; background: rgb(255, 255, 255);"><p style="text-align: center;"><span style="font-size: 16px;">（美国TSI8455）</span></p></td><td valign="center" style="padding: 0px; border-width: 1px; border-style: solid; border-color: initial; background: rgb(255, 255, 255);"><p style="text-align: center;"><span style="font-size: 16px;">（美国TSI8465）</span></p></td><td valign="center" style="padding: 0px; border-width: 1px; border-style: solid; border-color: initial; background: rgb(255, 255, 255);"><p style="text-align: center;"><span style="font-size: 16px;">（美国TSI8475）</span></p></td></tr><tr><td valign="center" style="padding: 0px; border-width: 1px; border-style: solid; border-color: initial; background: rgb(255, 255, 255);"><p style="text-align: center;"><span style="font-size: 16px;">*&nbsp;保护式探管</span></p></td><td valign="center" style="padding: 0px; border-width: 1px; border-style: solid; border-color: initial; background: rgb(255, 255, 255);"><p style="text-align: center;"><span style="font-size: 16px;">*&nbsp;较小的气流阻碍</span></p></td><td valign="center" style="padding: 0px; border-width: 1px; border-style: solid; border-color: initial; background: rgb(255, 255, 255);"><p style="text-align: center;"><span style="font-size: 16px;">*&nbsp;全方位探管</span></p></td></tr><tr><td valign="center" style="padding: 0px; border-width: 1px; border-style: solid; border-color: initial; background: rgb(255, 255, 255);"><p style="text-align: center;"><span style="font-size: 16px;">*&nbsp;结实的陶瓷传感器</span></p></td><td valign="center" style="padding: 0px; border-width: 1px; border-style: solid; border-color: initial; background: rgb(255, 255, 255);"><p style="text-align: center;"><span style="font-size: 16px;">*&nbsp;是有限空间的理想检测仪器</span></p></td><td valign="center" style="padding: 0px; border-width: 1px; border-style: solid; border-color: initial; background: rgb(255, 255, 255);"><p style="text-align: center;"><span style="font-size: 16px;">*&nbsp;可检测0.05&nbsp;米/&nbsp;秒以下的低风速</span></p></td></tr><tr><td valign="center" style="padding: 0px; border-width: 1px; border-style: solid; border-color: initial; background: rgb(255, 255, 255);"><p style="text-align: center;"><span style="font-size: 16px;">*&nbsp;应用范围广泛</span></p></td><td valign="center" style="padding: 0px; border-width: 1px; border-style: solid; border-color: initial; background: rgb(255, 255, 255);"><p style="text-align: center;"><span style="font-size: 16px;">*&nbsp;反应时间迅速</span></p></td><td valign="center" style="padding: 0px; border-width: 1px; border-style: solid; border-color: initial; background: rgb(255, 255, 255);"><p style="text-align: center;"><span style="font-size: 16px;">*&nbsp;是检测未知风向或多风向的理想仪器</span></p></td></tr><tr><td valign="center" style="padding: 0px; border-width: 