INSERT INTO `tp_test` VALUES ('524', '<p style="white-space: normal; text-indent: 2em; line-height: 2em;"><span style="font-size: 16px;">&nbsp; HM685型平衡阀流量测试仪用于平衡液体循环加热和冷却系统、检查泵的性能和设置平衡阀，能够在无需改变软管连接或仪器阀门设置的条件下，同时测量和显示压差、高压侧和低压侧压力。</span></p><p style="white-space: normal; text-indent: 2em; line-height: 2em;"><span style="font-size: 16px;">&nbsp; 仪器具有简单的设置和直观的操作，非常易于使用。</span></p><div><span style="font-size: 16px;"><br/></span></div><p><br/></p>', '<h3 style="line-height: 2em;"><span style="font-size: 16px;">组件包括：</span></h3><p style="line-height: 2em;"><span style="font-size: 16px;">·&nbsp;仪器主机</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">·&nbsp;硬质手提箱</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">·&nbsp;(2) 6.7 ft x ¼-in. 具有截止阀的(2 m x 6 mm) 软管</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">·&nbsp;(2) B&amp;G读出探头</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">·&nbsp;(2) P/T表适配探头</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">·&nbsp;电源线</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">·&nbsp;温度探头</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">·&nbsp;CompuDat™ USB下载软件</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">·&nbsp;USB数据线</span></p><p style="line-height: 2em;"><span style="font-size: 16px;"><br/></span></p><p style="line-height: 2em;"><strong><span style="font-size: 16px;">特点与优势</span></strong></p><p style="line-height: 2em;"><span style="font-size: 16px;">支持双温度探头输入</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">背光大屏，在昏暗光线条件下仍能清晰显示</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">由四节碱性或镍氢可充电电池供电</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">读数方式&nbsp; H 2 O, ft. H 2 O, psi, in. Hg, mm Hg, m H 2 O, kPa 或 bar</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">压差范围&nbsp; ±300 psi (±2068 kPa)</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">表压范围&nbsp; -20 - 300 psi (-138 - 2068 kPa)&nbsp;</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">集成通用流量和btu/hr计算</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">当设置Cv (Kv) 因子后显示体积流量</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">可输入多达100 个Cv (Kv)因子</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">计算制动力，热流， Cv (Kv)因子和泵轮尺寸</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">能够存储多达4,000个数据点，用于检索或通过USB端口下载</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">坚固的防喷溅仪器箱</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">含电池重量：2.65 lbs (1.20 kg)</span></p><p><br/></p>', '<p style="line-height: 2em;"><strong><span style="font-size: 16px;">技术参数：</span></strong></p><p style="line-height: 2em;"><span style="font-size: 16px;">压力测量量程：&nbsp;-300 to 300 psi (-2,068 to 2,068 kPa) </span></p><p style="line-height: 2em;"><span style="font-size: 16px;">测量范围：&nbsp;-20 to 300 psi (-138 to 2,068 kPa) (-40 to 610 in. Hg) </span></p><p style="line-height: 2em;"><span style="font-size: 16px;">分辨率（最佳）：0.001 psi (0.01 kPa) (0.01 in. Hg)</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">精度1 ：读数的±1% .072 psi (0.5 kPa) (0.15 in. Hg) </span></p><p style="line-height: 2em;"><span style="font-size: 16px;">测量单位：&nbsp;psi, in. H2O, ft H2O, kPa, mm Hg, in. Hg, m H2O, bar </span></p><p style="line-height: 2em;"><span style="font-size: 16px;">压力管接头：&nbsp;¼” 37• flare fitting, Male </span></p><p style="line-height: 2em;"><span style="font-size: 16px;">温度</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">操作温度&nbsp;(电子) 40 to 113°F (5 to 45°C) </span></p><p style="line-height: 2em;"><span style="font-size: 16px;">存储：&nbsp;-4 to 140°F (-20 to 60°C) </span></p><p style="line-height: 2em;"><span style="font-size: 16px;">液体介质：&nbsp;32 to 180°F (0 to 82°C) </span></p><p style="line-height: 2em;"><span style="font-size: 16px;">探针&nbsp;(浸入式) -40 to 250°F (-40 to 121 °C) </span></p><p style="line-height: 2em;"><span style="font-size: 16px;">分辨率：&nbsp;0.1°F (0.1°C)</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">精度：&nbsp;±0.5% of 读数+1.2°F (0.7 °C)</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">单位：&nbsp;°F, °C </span></p><p style="line-height: 2em;"><span style="font-size: 16px;">流量范围2：&nbsp;-22,712 to 22,271 m3/h, -99,999 to 99,999 USGPM (-6,309 to 6,309 l/s) </span></p><p style="line-height: 2em;"><span style="font-size: 16px;">分辨率（最佳）：0.0001 USGPM (0.00001 l/s)</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">精度：每压力精度+阀门偏差</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">单位：&nbsp;USGPM, UKGPM, m3/h, l/s, l/m </span></p><p style="line-height: 2em;"><span style="font-size: 16px;">测量周期：用户可选择&nbsp;(1, 5, 10, 20, and 30 秒) </span></p><p style="line-height: 2em;"><span style="font-size: 16px;">数据统计：最小值 ,最大值, 平均值, 总计个4,000 计数</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">数据存储：&nbsp;4,000 combined readings, 100 Test IDs </span></p><p style="line-height: 2em;"><span style="font-size: 16px;">记录间隔：&nbsp;用户可选择(1 to 3,600 秒) </span></p><p style="line-height: 2em;"><span style="font-size: 16px;">尺寸：&nbsp;11.1 in. × 4.7 in. × 3.5 in. (28.2 cm × 11.9 cm × 8.8 cm) </span></p><p style="line-height: 2em;"><span style="font-size: 16px;">重量：&nbsp;2.65 lbs (1.20 kg) </span></p><p style="line-height: 2em;"><span style="font-size: 16px;">电源适配器：4节AAs, or AC 适配器</span></p><p><br/></p>');
INSERT INTO `tp_test` VALUES ('525', '<p style="text-indent: 2em; line-height: 2em;"><span style="font-size: 16px;">&nbsp; 六喷嘴雾化器9306是用来产生用于PIV和LDV测量的种子颗粒浓度高的多分散气溶胶。</span></p><p style="text-indent: 2em; line-height: 2em;"><span style="font-size: 16px;">&nbsp; 该系统具有一个内置的压力调节器和压力表，以及作为一个独立的稀释系统。外部控制允许你通过六粒子产生雾化射流可供选择，每个生产颗粒浓度大于10&nbsp;7个每立方厘米，在6.5升/分钟（名义在25 psig的压力），允许粒子数浓度和颗粒的总输出进行调整。该系统可用于从几乎任何液体产生的粒子，甚至可以从溶液或悬浮液中固体粒子的产生。稀释空气流量的精确控制，使得在输出浓度进一步控制和从一个解决方案生成有助于干燥的固体颗粒</span></p><p><br/></p>', '<h3 style="line-height: 2em;"><span style="font-size: 16px;">应用</span></h3><p style="line-height: 2em;"><span style="font-size: 16px;">·&nbsp;粒子图像测速实验</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">·&nbsp;激光多普勒测速实验</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">·&nbsp;研究需要的多分散性，高浓度的气溶胶</span></p><p><br/></p>', '');
INSERT INTO `tp_test` VALUES ('526', '<p style="text-indent: 2em; line-height: 2em;"><span style="font-size: 16px;">&nbsp; TSI的9302型单喷嘴雾化器可以通过简单的压缩空气源产生多分散气溶胶流，是一种非常经济有效仪器。此仪器也是TSI系列产品中最简单的雾化器，内置用于控制外部空气源的压力调节器。</span></p><p style="text-indent: 2em; line-height: 2em;"><span style="font-size: 16px;">&nbsp; 9302型单喷嘴雾化器能够以6.5 L/min的额定流量产生浓度超过107个/cm3的粒子，相当于4~5μm的平均液滴粒径（D10）和40000粒子/cc的浓度，是LDV和PDPA系统对准的理想选择，且特别适合三组分系统。上述值是175千帕（25磅平方英寸）进水压力下的典型值。用户可以通过方便的压力控制和灵活的出口管为应用生成合适量的气溶胶。用户还可以通过半透明的储液罐准确地看到装置中有多少液体。</span></p><p><br/></p>', '<h3 style="line-height: 2em;"><span style="font-size: 16px;">应用</span></h3><p style="line-height: 2em;"><span style="font-size: 16px;">·&nbsp;LDV</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">·&nbsp;PDPA</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">·&nbsp;激光设置和对齐</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">·&nbsp;仪器校准</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">·&nbsp;要求多分散、高浓度气溶胶的研究</span></p><p style="line-height: 2em;"><span style="font-size: 16px;"><br/></span></p><h3 style="line-height: 2em;"><span style="font-size: 16px;">包含组件</span></h3><p style="line-height: 2em;"><span style="font-size: 16px;">·&nbsp;雾化器</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">·&nbsp;手册</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">·&nbsp;灵活的出口管</span></p><p style="line-height: 2em;"><span style="font-size: 16px;"><br/></span></p><p style="line-height: 2em;"><strong><span style="font-size: 16px;">特点与优势</span></strong></p><p style="line-height: 2em;"><span style="font-size: 16px;">浓度超过107粒子/cm3</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">标称流量为6.5 L/min</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">可靠</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">性价比高</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">结构紧凑</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">方便的压力控制</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">方便灵活的出口管</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">易于填充和清洁</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">&nbsp;</span></p><p><br/></p>', '');
INSERT INTO `tp_test` VALUES ('528', '<p style="text-indent: 2em; line-height: 2em;"><span style="font-size: 16px;">&nbsp; TSI 的 3340A 型激光气溶胶粒径谱仪是一种通用的气溶胶 粒径谱仪，具有超高灵敏度、高分辨率和易用性。这种高性 能的实用工具能够在 0.09（90nm）-7.5µm 的粒径范围内 以十分之一秒的速度测量完整的粒径分布。用户可以通过可配置的粒径通道在特定粒径范围内调零或匹配另一台仪器 的分辨率。</span></p><p><br/></p>', '<p style="line-height: 2em;"><strong><span style="font-size: 16px;">功能和优点</span></strong><span style="font-size: 16px;"> </span></p><p style="line-height: 2em;"><span style="font-size: 16px;">超高灵敏度和高分辨率 </span></p><p style="line-height: 2em;"><span style="font-size: 16px;">+ 动态粒径范围：0.09-7.5µm + 0.1µm 的典型分辨率在粒子直径的 2.5% 以内 </span></p><p style="line-height: 2em;"><span style="font-size: 16px;">+ 宽浓度范围：高达 18000 粒 /cm3 易用性和灵活性 </span></p><p style="line-height: 2em;"><span style="font-size: 16px;">+ 100 个用户可配置粒径通道 </span></p><p style="line-height: 2em;"><span style="font-size: 16px;">+ 用户可调流量 </span></p><p style="line-height: 2em;"><span style="font-size: 16px;">+ 直观的基于 LabVIEW 的控制软件 </span></p><p style="line-height: 2em;"><span style="font-size: 16px;">+ 带触屏和键盘的 Microsoft Surface Pro 平板电脑 业内最先进的光学和检测系统 </span></p><p style="line-height: 2em;"><span style="font-size: 16px;">+ 专利的广角光学和腔内激光技术</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">+ 高灵敏度光电检测器 </span></p><p style="line-height: 2em;"><span style="font-size: 16px;">+ 自动增益比调整和激光基准补偿</span></p><p style="line-height: 2em;"><strong><span style="font-size: 16px;">&nbsp;</span></strong></p><p style="line-height: 2em;"><strong><span style="font-size: 16px;">特色应用</span></strong><span style="font-size: 16px;">&nbsp;</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">3340A 型激光气溶胶粒径谱仪适用于广泛的应用领域。下面重点介绍了一些： </span></p><p style="line-height: 2em;"><span style="font-size: 16px;">+ 过滤器测试：高分辨率、宽浓度范围、低端粒径检 测极限和快速测量时间使 3340A 非常适合过滤器 测试应用。</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">&nbsp;-过滤器效率测试 </span></p><p style="line-height: 2em;"><span style="font-size: 16px;">-磁盘驱动器过滤器测试 </span></p><p style="line-height: 2em;"><span style="font-size: 16px;">-磁盘驱动器的开发与研究</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">+ 室内空气质量：激光气溶胶粒径谱仪作为一种单机仪器，在广泛的浓度范围内提供高分辨率的粒径信 息。将 3340A 型号与 CPC 配对，您可以轻松、实时地测量纳米粒径分数（&lt;100纳米）。</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">+ 大气研究和环境监测：仪器粒径范围，测量时间，和缺乏放射性源或工作流体非常适合在飞机上使用 或环境采样点转移。 </span></p><p style="line-height: 2em;"><span style="font-size: 16px;">+ 吸入毒理学和暴露监测：易用性、快速测量时间和 测量已知成分气溶胶的准确度使其成为激光气溶胶 粒径谱仪的常见应用。</span></p><p style="line-height: 2em;"><span style="font-size: 16px;"><br/></span></p><p>其他应用&nbsp;</p><p style="line-height: 2em;"><span style="font-size: 16px;">+ 一般气溶胶研究 </span></p><p style="line-height: 2em;"><span style="font-size: 16px;">+ 仪器校准 </span></p><p style="line-height: 2em;"><span style="font-size: 16px;">+ 生物危害检测 </span></p><p style="line-height: 2em;"><span style="font-size: 16px;">+ 过程监控</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">+ 药物研究 </span></p><p style="line-height: 2em;"><span style="font-size: 16px;">+ 粉末和食品研 </span></p><p style="line-height: 2em;"><span style="font-size: 16px;">+ 燃烧和排放研究 </span></p><p style="line-height: 2em;"><span style="font-size: 16px;">+ 喷雾分析 </span></p><p style="line-height: 2em;"><span style="font-size: 16px;">+ 凝结和成核研究</span></p><p><br/></p>', '<p><strong><span style="font-size: 16px;">规格</span></strong></p><p><strong><span style="font-size: 16px;"><br/></span></strong></p><p><span style="font-size: 16px;">粒径范围</span></p><p><span style="font-size: 16px;">&nbsp;0.09 - 7.5mm </span></p><p><span style="font-size: 16px;">粒子粒径测量精度&nbsp;0.1 微米（通常在 2.5% 以内）的颗粒直径 5% 范围内 </span></p><p><span style="font-size: 16px;">零计数&nbsp;</span></p><p><span style="font-size: 16px;">&lt;1@5分钟内计数（日本工业标准）</span></p><p><span style="font-size: 16px;">计数效率 &nbsp;</span></p><p><span style="font-size: 16px;">90nm时&gt;50% </span></p><p><span style="font-size: 16px;">粒子浓度范围</span></p><p><span style="font-size: 16px;">10 cm3 /min 为 18,000 粒子 /cm3 </span></p><p><span style="font-size: 16px;">50 cm3 /min 为 3,600 粒子 /cm3 </span></p><p><span style="font-size: 16px;">95 cm3 /min 为 1,800 粒子 /cm3</span></p><p><span style="font-size: 16px;">通道数 </span></p><p><span style="font-size: 16px;">用户可选择，最多 100 个</span></p><p><span style="font-size: 16px;">流动&nbsp;</span></p><p><span style="font-size: 16px;">鞘气速度 用户可选择，10-95 cm3 /min. ± 5% </span></p><p><span style="font-size: 16px;">鞘流速度 650 cm3 /min. ± 5% </span></p><p><span style="font-size: 16px;">大气压力校正 由内部流量控制器自动校正样品流量。 </span></p><p><span style="font-size: 16px;">环境运行条件&nbsp;</span></p><p><span style="font-size: 16px;">工作温度 10 - 30℃&nbsp;(50 - 86°F) </span></p><p><span style="font-size: 16px;">工作湿度 相对湿度 10 - 90% </span></p><p><span style="font-size: 16px;">非凝结 </span></p><p><span style="font-size: 16px;">工作高度 海平面 - 4,000 m(13,000 ft) </span></p><p><span style="font-size: 16px;">气溶胶介质&nbsp;</span></p><p><span style="font-size: 16px;">设计用于空气。请勿与加压、爆炸性、腐蚀性、有毒或其 他有害气体一起使用。 校准粒子</span></p><p><span style="font-size: 16px;">NIST 可溯源聚苯乙烯乳胶（PSL）球体 </span></p><p><span style="font-size: 16px;">激光光源&nbsp;</span></p><p><span style="font-size: 16px;">氦氖（HeNe）气体激光器，633nm，&gt;1w 腔内功率</span></p><p><span style="font-size: 16px;">探测器</span></p><p><span style="font-size: 16px;">雪崩光电二极管 (APD) 和 PIN 光电二极管 </span></p><p><span style="font-size: 16px;">用户界面</span></p><p><span style="font-size: 16px;">在带有 12.3 英寸触摸屏和键盘的 Microsoft Surface Pro 平板电脑上运行的控制软件</span></p><p><span style="font-size: 16px;">操作系统和软件&nbsp;</span></p><p><span style="font-size: 16px;">Windows 10® 基于 LabVIEW 2017 生成的可执行控制软件 VI（虚拟仪 器） </span></p><p><span style="font-size: 16px;">通信&nbsp;</span></p><p><span style="font-size: 16px;">用于仪表控制的 RS-232（9 针 D 接头），带有 NI 串行到USB转换器，与控制计算机接口 </span></p><p><span style="font-size: 16px;">尺寸&nbsp;</span></p><p><span style="font-size: 16px;">56 x 43 x 25 cm (22 x 17 x 10 in) </span></p><p><span style="font-size: 16px;">重量 24 kg (53 lbs.) </span></p><p><span style="font-size: 16px;">功率&nbsp;100-240 VAC; 50/60 Hz; 200 W</span></p><p><br/></p>');
INSERT INTO `tp_test` VALUES ('530', '<p style="text-indent: 2em; line-height: 2em;"><span style="font-size: 16px;">&nbsp; 大型粒子气溶胶发生器8108主要用于过滤测试, 可在直径为0.1 至10μm 的宽粒径范围内产生高浓度气溶胶。它生产氯化钾 (KCl) 或其他气溶胶, 并满足已建立的 isose 11155-1 和 ASHRAE 52.2 测试代码的要求, 以及拟议的 ISO 16890 第2部分代码。专为易于清洁而设计, 可提供准确且可重复的结果。</span></p><p style="text-indent: 2em; line-height: 2em;"><span style="font-size: 16px;">&nbsp; 此款气溶胶发生器特别适于车内空气过滤器和常见通风过滤器的分离效率测试。</span></p><p><br/></p>', '<h3><span style="font-size: 16px;">应用范围</span></h3><p><span style="font-size: 16px;">·&nbsp;气溶胶生成</span></p><p><span style="font-size: 16px;">·&nbsp;过滤器测试</span></p><p><span style="font-size: 16px;"><br/></span></p><h3><span style="font-size: 16px;">所含项</span></h3><p><span style="font-size: 16px;">·&nbsp;加热器</span></p><p><span style="font-size: 16px;">·&nbsp;干燥塔</span></p><p><span style="font-size: 16px;">·&nbsp;非放射性气溶胶中和器</span></p><p><span style="font-size: 16px;"><br/></span></p><p><strong><span style="font-size: 16px;">特点与优势</span></strong></p><p><span style="font-size: 16px;">生成粒径范围 0.1 至 10 µm 的高浓度气溶胶粒子</span></p><p><span style="font-size: 16px;">&nbsp;</span></p><p><br/></p>', '');
INSERT INTO `tp_test` VALUES ('532', '<p style="text-indent: 2em;"><span style="font-size: 16px;">&nbsp;此人工尘发生器可满足国家标准GB/T14295-2008《空气过滤器》、美国标准ASHRAE52.1-1992和欧洲标准EN779:2012中关于空气过滤器容尘量实验的要求而设计的。</span></p><p style="text-indent: 2em;"><span style="font-size: 16px;">&nbsp;</span></p><p style="text-indent: 2em;"><span style="font-size: 16px;">&nbsp;适合尘源：ASHRAE尘（72%道路尘、23%碳黑、5%知棉绒）、A2细灰、氧化铝粉和滑石粉等，发尘量：0-500g/h可调，压缩空气要求：干燥洁净，最大压力0.4MPa，电源要求：220V、50Hz。</span></p><p><br/></p>', '', '');
INSERT INTO `tp_test` VALUES ('535', '<p style="line-height: 2em;"><span style="font-size: 16px;"></span></p><p style="line-height: 2em;"><span style="font-size: 16px;">该设备可满足ANSI/ASHRAE52.2-2007、GB/T 14295-2008和JG/T 294-2010对气溶胶尘源的要求。</span><br/></p><p style="line-height: 2em;"><span style="font-size: 16px;">所用气溶胶KCI/NaCI（质量浓度：1%～23%），也可用于发生PSL、DEHS气溶胶等。</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">粒径范围：0.1µm~10.0µm。</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">浓度：0.3µm～1.0µm&gt;103P/.1.0µm—10.0µm&gt;102P/L。</span></p><p style="line-height: 2em;"><span style="font-size: 16px;">适用于风量范围：&gt;500m3/h。</span></p><p><br/></p>', '<p style="white-space: normal;"><strong><span style="font-size: 16px;">大颗粒气溶胶发生器</span></strong></p><p style="white-space: normal;"><strong><span style="font-size: 16px;">型号：EMCSO-P015</span></strong></p><p style="white-space: normal; line-height: 2em;"><span style="font-size: 16px;">大颗粒气溶胶发生器,该设备可满足ANSI/ASHRAE52.2-2007、GB/T 14295-2008和JG/T 294-2010对气溶胶尘源的要求。所用气溶胶KCI/NaCI（质量浓度：1%～23%），也可用于发生PSL、DEHS气溶胶等。</span></p><p><br/></p>', '<p style="white-space: normal; line-height: 2em;"><span style="font-size: 16px;">该设备可满足ANSI/ASHRAE52.2-2007、GB/T 14295-2008和JG/T 294-2010对气溶胶尘源的要求。</span><br/></p><p style="white-space: normal; line-height: 2em;"><span style="font-size: 16px;">所用气溶胶KCI/NaCI（质量浓度：1%～23%），也可用于发生PSL、DEHS气溶胶等。</span></p><p style="white-space: normal; line-height: 2em;"><span style="font-size: 16px;">粒径范围：0.1µm~10.0µm。</span></p><p style="white-space: normal; line-height: 2em;"><span style="font-size: 16px;">浓度：0.3µm～1.0µm&gt;103P/.1.0µm—10.0µm&gt;102P/L。</span></p><p style="white-space: normal; line-height: 2em;"><span style="font-size: 16px;">适用于风量范围：&gt;500m3/h。</span></p><p><br/></p>');
INSERT INTO `tp_test` VALUES ('536', '<p style="text-indent: 0em;"><span style="font-size: 16px;">该气溶胶发生器可广泛用于实验室空气过滤器和高效空气过滤器检测，也可用于现场高效空气过滤器的检漏试验。</span></p><p style="text-indent: 0em;"><span style="font-size: 16px;">发生气溶胶的粒径范围为：15nm~620nm，中值直径在100nm~200nm之间，工作压力为0.01MPa～0.4MPa，单个喷嘴1000m3/h风量下，发生DOS气溶胶的最大浓度可达到9.00E+05粒/cm3.</span></p><p><br/></p>', '<p style="text-indent: 0em;"><span style="font-size: 16px;">该气溶胶发生器可广泛用于实验室空气过滤器和高效空气过滤器检测，也可用于现场高效空气过滤器的检漏试验。</span></p><p style="text-indent: 0em;"><span style="font-size: 16px;">发生气溶胶的粒径范围为：15nm~620nm，中值直径在100nm~200nm之间，工作压力为0.01MPa～0.4MPa，单个喷嘴1000m3/h风量下，发生DOS气溶胶的最大浓度可达到9.00E+05粒/cm3.</span></p><p><br style="white-space: normal;"/></p><p><br/></p>', '<p style="text-indent: 0em;"><span style="font-size: 16px;">该气溶胶发生器可广泛用于实验室空气过滤器和高效空气过滤器检测，也可用于现场高效空气过滤器的检漏试验。</span></p><p style="text-indent: 0em;"><span style="font-size: 16px;">发生气溶胶的粒径范围为：15nm~620nm，中值直径在100nm~200nm之间，工作压力为0.01MPa～0.4MPa，单个喷嘴1000m3/h风量下，发生DOS气溶胶的最大浓度可达到9.00E+05粒/cm3.</span></p><p><br style="white-space: normal;"/></p><p><br/></p>');
INSERT INTO `tp_test` VALUES ('581', '<p style="white-space: normal; line-height: 3em;"><span style="font-size: 14px;">l&nbsp;方便的成套解决方案，即有单通道测量PM10、PM2.5的方案，也有多通道同时测量总PM、PM 10、PM2.5以及PM1.0的方案</span></p><p style="white-space: normal; line-height: 3em;"><span style="font-size: 14px;">l&nbsp;坚固耐用，允许在-20℃-50℃环境下长时间运行</span></p><p style="white-space: normal; line-height: 3em;"><span style="font-size: 14px;">l&nbsp;特制的超长寿命采样泵，大幅提高长期连续测量时间上限（寿命预计）10000小时），并可现场更换</span></p><p style="white-space: normal; line-height: 3em;"><span style="font-size: 14px;">l&nbsp;可选加热除湿进样调节器，能够在环境相对湿度&gt;50%的条件下保证测量精度</span></p><p style="white-space: normal; line-height: 3em;"><span style="font-size: 14px;">l&nbsp;通过无线数据采集和传输模块和云数据管理系统能够实时显示数据和趋势曲线并有多功能报警系统</span></p><p><br/></p>', '<p style="line-height: 2em;"><strong><span style="font-size: 14px;">粉尘在线监测仪&nbsp;</span></strong></p><p style="line-height: 2em;"><strong><span style="font-size: 14px;">型号：8540/8543</span></strong></p><p style="line-height: 2em;"><span style="font-size: 14px;">该Envrironmental DustTrak粉尘在线监测仪基于DustTrak技术设计，每天被数以千计的人使用，该系统是一种用于长期户外环境监测的可靠、易读和精确的解决方案。三种常用系统配置，你可以根据您想要测量的粒径和质量浓度选择合适的配置。紧凑、全天侯的监测箱内部有新款增强型Environmental DustTrak&nbsp;光度计以及其他zui新设计的养分组件，包括长寿命泵、内置自动调零模块、选配加热除湿进样调节器以及内置加热保温组件。并将以上组件整合到云数据管理系统中，Environmental DustTrak监测仪是提供实时粉尘测量数据的zui有效、<span style="font-size: 14px;">zui</span>灵活和<span style="font-size: 14px;">zui</span>具性价比的解决方案。</span></p><p style="line-height: 2em;"><span style="font-size: 14px;">&nbsp;</span></p><p style="line-height: 2em;"><strong><span style="font-size: 14px;">特性和优点</span></strong></p><p style="line-height: 2em;"><span style="font-size: 14px;">l&nbsp;方便的成套解决方案，即有单通道测量PM10、PM2.5的方案，也有多通道同时测量总PM、PM 10、PM2.5以及PM1.0的方案</span></p><p style="line-height: 2em;"><span style="font-size: 14px;">l&nbsp;坚固耐用，允许在-20℃-50℃环境下长时间运行</span></p><p style="line-height: 2em;"><span style="font-size: 14px;">l&nbsp;特制的超长寿命采样泵，大幅提高长期连续测量时间上限（寿命预计）10000小时），并可现场更换</span></p><p style="line-height: 2em;"><span style="font-size: 14px;">l&nbsp;可选加热除湿进样调节器，能够在环境相对湿度&gt;50%的条件下保证测量精度</span></p><p style="line-height: 2em;"><span style="font-size: 14px;">l&nbsp;通过无线数据采集和传输模块和云数据管理系统能够实时显示数据和趋势曲线并有多功能报警系统</span></p><p style="line-height: 2em;"><span style="font-size: 14px;">&nbsp;</span></p><p style="line-height: 2em;"><strong><span style="font-size: 14px;">应用</span></strong></p><p style="line-height: 2em;"><span style="font-size: 14px;">l&nbsp;无组织排放监测</span></p><p style="line-height: 2em;"><span style="font-size: 14px;">l&nbsp;现场周界监测</span></p><p style="line-height: 2em;"><span style="font-size: 14px;">l&nbsp;围栏监测</span></p><p style="line-height: 2em;"><span style="font-size: 14px;">l&nbsp;环境治理监测</span></p><p style="line-height: 2em;"><span style="font-size: 14px;">l&nbsp;建筑工地和矿区监测</span></p><p style="line-height: 2em;"><span style="font-size: 14px;">l&nbsp;危险废弃物堆放点监测</span></p><p style="line-height: 2em;"><span style="font-size: 14px;">l&nbsp;粉尘控制操作监测</span></p><p style="line-height: 2em;"><span style="font-size: 14px;">&nbsp;</span></p><p style="line-height: 2em;"><strong><span style="font-size: 14px;">可选组件</span></strong></p><p style="line-height: 2em;"><span style="font-size: 14px;">加热除湿进行调节器</span></p><p style="line-height: 2em;"><span style="font-size: 14px;">通过调节进样气溶胶以提高测量准确度，当环境相对湿度持续高于50%时，将湿度对测量结果的影响降至最低</span></p><p style="line-height: 2em;"><strong><span style="font-size: 14px;">可充电电池供电系统</span></strong></p><p style="line-height: 2em;"><span style="font-size: 14px;">当无法外接交流电源时，能够向DustTrak基础系统提供持续电力。包括2套22Ah可充电电池和充电器</span></p><p style="line-height: 2em;"><strong><span style="font-size: 14px;">太阳能供电系统</span></strong></p><p style="line-height: 2em;"><span style="font-size: 14px;">向Environmental DustTrak系统提供持续电力。包括2片90W太阳能板（含支架），全气候电池以及充电调节器外箱，充电调节器，100Ah可充电电池以及直流电源线</span></p><p style="line-height: 2em;"><strong><span style="font-size: 14px;">立柱安装组件</span></strong></p><p style="line-height: 2em;"><span style="font-size: 14px;">包括支架，工具以及安装带，用于将Environmental DustTrak监测箱安装到一个直径为100-150mm的固定柱上（非标配）</span></p><p style="line-height: 2em;"><strong><span style="font-size: 14px;">隔热板</span></strong></p><p style="line-height: 2em;"><span style="font-size: 14px;">定制金属罩隔离日光照射。推荐在环境&gt;40℃，日光直晒的场所.</span></p><p style="line-height: 2em;"><strong><span style="font-size: 14px;">计量传感器</span></strong></p><p style="line-height: 2em;"><span style="font-size: 14px;">我们提供两种计量传感器。基本型测量温度、湿度和压力，高级型除温湿度和压力测量外，还可以测量风速 和风向。两种型号都提供测试、电源连接和安装硬件。</span></p><p><br/></p>', '<table cellspacing="0" cellpadding="0" width="968" style="width: 968px;"><tbody><tr class="firstRow"><td valign="top" style="border: 1px solid rgb(0, 0, 0); padding: 0px;" width="144"><p style="line-height: 1.75em;"><span style="font-size: 14px;">型号</span></p></td><td valign="top" style="border: 1px solid rgb(0, 0, 0); padding: 0px;" width="158"><p style="line-height: 1.75em;"><span style="font-size: 14px;">PM10</span></p></td><td valign="top" style="border: 1px solid rgb(0, 0, 0); padding: 0px;" width="158"><p style="line-height: 1.75em;"><span style="font-size: 14px;">PM2.5</span></p></td><td valign="top" style="border: 1px solid rgb(0, 0, 0); padding: 0px;" width="507"><p style="line-height: 1.75em;"><span style="font-size: 14px;">DRX</span></p></td></tr><tr><td valign="top" style="border: 1px solid rgb(0, 0, 0); padding: 0px;" width="144"><p style="line-height: 1.75em;"><span style="font-size: 14px;">测量粒径等级</span></p></td><td valign="top" style="border-width: 1px; border-style: solid; border-color: rgb(0, 0, 0); padding: 0px;" width="158"><p style="line-height: 1.75em;"><span style="font-size: 14px;">PM10</span></p></td><td valign="top" style="border-width: 1px; border-style: solid; border-color: rgb(0, 0, 0); padding: 0px;" width="158"><p style="line-height: 1.75em;"><span style="font-size: 14px;">PM2.5</span></p></td><td valign="top" style="border-width: 1px; border-style: solid; border-color: rgb(0, 0, 0); padding: 0px;" width="507"><p style="line-height: 1.75em;"><span style="font-size: 1