INSERT INTO `tp_test` VALUES ('572', '<p style="line-height: 3em;"><span style="font-size: 14px;">5300&nbsp;<span style="font-family: 宋体;">系列是</span>&nbsp;TSI&nbsp;<span style="font-family: 宋体;">公司出品的高流量热式质量流量计。</span>5300&nbsp;<span style="font-family: 宋体;">系列可以以超低压降测量高流</span>&nbsp;<span style="font-family: 宋体;">量，最高可测量</span>&nbsp;300 L/min<span style="font-family: 宋体;">，同时最大限度地减少背压及其对系统的影响。与其他以类似</span>&nbsp;<span style="font-family: 宋体;">流速测量的仪器相比，</span>5300&nbsp;<span style="font-family: 宋体;">系列流量计外形轻巧紧凑。</span>5300&nbsp;<span style="font-family: 宋体;">系列设计为通用流量计，并</span>&nbsp;<span style="font-family: 宋体;">针对在呼吸设备市场中的应用进行了优化</span></span></p>', '<p style="line-height: 2em;"><span style="font-size: 14px;">TSI <span style="font-family: 宋体;">的热式质量流量计采用专利的铂金镀膜传感器设计，这种设计适</span> <span style="font-family: 宋体;">用于要求快速响应、低压损和高精度，流量范围宽的多种气体流量</span> <span style="font-family: 宋体;">测量的应用。</span></span></p><p style="line-height: 2em;"><span style="font-size: 14px;"><span style="font-size: 14px; font-family: 宋体;">高级机型可以将质量流量、绝对压力、温度、体积、压差和湿度测量集成到一台设</span> <span style="font-size: 14px; font-family: 宋体;">备中，还提供具有更高流量精度、数据记录或湿度补偿流量测量规格选项的机型。</span></span></p><p style="line-height: 2em;"><span style="font-size: 14px;">5300 <span style="font-size: 14px; font-family: 宋体;">系列是</span> TSI <span style="font-size: 14px; font-family: 宋体;">公司出品的高流量热式质量流量计。</span>5300 <span style="font-size: 14px; font-family: 宋体;">系列可以以超低压降测量高流</span> <span style="font-size: 14px; font-family: 宋体;">量，最高可测量</span> 300 L/min<span style="font-size: 14px; font-family: 宋体;">，同时最大限度地减少背压及其对系统的影响。与其他以类似</span> <span style="font-size: 14px; font-family: 宋体;">流速测量的仪器相比，</span>5300 <span style="font-size: 14px; font-family: 宋体;">系列流量计外形轻巧紧凑。</span>5300 <span style="font-size: 14px; font-family: 宋体;">系列设计为通用流量计，并</span> <span style="font-size: 14px; font-family: 宋体;">针对在呼吸设备市场中的应用进行了优化</span></span></p><p style="line-height: 2em;"><span style="font-size: 14px;">&nbsp;</span></p><p style="line-height: 2em;"><strong><span style="font-family: 宋体; font-size: 14px;">功能与优点</span></strong></p><p style="line-height: 2em;"><span style="font-size: 14px;">&nbsp;+ 4 <span style="font-size: 14px; font-family: 宋体;">毫秒双向流量响应</span></span></p><p style="line-height: 2em;"><span style="font-size: 14px;">+ <span style="font-size: 14px; font-family: 宋体;">高精度（读数的</span> ±2%<span style="font-size: 14px; font-family: 宋体;">，可选读数的</span> ±1.7% <span style="font-size: 14px; font-family: 宋体;">可选）</span></span></p><p style="line-height: 2em;"><span style="font-size: 14px;">+ <span style="font-size: 14px; font-family: 宋体;">低压降使背压最小化</span></span></p><p style="line-height: 2em;"><span style="font-size: 14px;">+ <span style="font-size: 14px; font-family: 宋体;">宽广泛的动态工作范围（</span>1000:1 <span style="font-size: 14px; font-family: 宋体;">调节比）</span></span></p><p style="line-height: 2em;"><span style="font-size: 14px;">+ <span style="font-size: 14px; font-family: 宋体;">多种气体校准，可供用户选择</span></span></p><p style="line-height: 2em;"><span style="font-size: 14px;">+ <span style="font-size: 14px; font-family: 宋体;">使用单个仪器的流量，绝对压力，温度，体积，</span> <span style="font-size: 14px; font-family: 宋体;">压差和湿度测量多达</span> 6 <span style="font-size: 14px; font-family: 宋体;">个参数</span></span></p><p style="line-height: 2em;"><span style="font-size: 14px;">+ <span style="font-size: 14px; font-family: 宋体;">温度和压力补偿，可选湿度补偿</span></span></p><p style="line-height: 2em;"><span style="font-size: 14px;">+ 2.8 <span style="font-size: 14px; font-family: 宋体;">英寸彩色触摸屏操作</span></span></p><p style="line-height: 2em;"><span style="font-size: 14px;">+ <span style="font-size: 14px; font-family: 宋体;">可同时显示四个测量参数</span></span></p><p style="line-height: 2em;"><span style="font-size: 14px;">+ <span style="font-size: 14px; font-family: 宋体;">可配置管端接头</span></span></p><p style="line-height: 2em;"><span style="font-size: 14px;">+ <span style="font-size: 14px; font-family: 宋体;">数据记录（选项）</span></span></p><p style="line-height: 2em;"><span style="font-size: 14px;">+ 