INSERT INTO `tp_test` VALUES ('585', '<p style="white-space: normal; line-height: 3em;"><strong><span style="font-family: 宋体; font-size: 14px;">主要性能</span></strong></p><p style="white-space: normal; line-height: 3em;"><span style="font-family: 宋体; font-size: 14px;">简单易用</span></p><p style="white-space: normal; line-height: 3em;"><span style="font-size: 14px;"><span style="font-family: 宋体;">背光灯图形</span>LCD<span style="font-family: 宋体;">显示屏</span></span></p><p style="white-space: normal; line-height: 3em;"><span style="font-size: 14px;"><span style="font-family: 宋体;">具有</span>A<span style="font-family: 宋体;">频率计权和</span>C<span style="font-family: 宋体;">频率计权</span>,<span style="font-family: 宋体;">具有快速和慢速时间计权</span></span></p><p style="white-space: normal; line-height: 3em;"><span style="font-size: 14px;"><span style="font-family: 宋体;">具有最大声级测量</span>Lmax<span style="font-family: 宋体;">以及峰值测量</span>Cpeak</span></p><p style="white-space: normal; line-height: 3em;"><span style="font-size: 14px;"><span style="font-family: 宋体;">用户可调门限</span>(CR:308)</span></p><p style="white-space: normal; line-height: 3em;"><span style="font-size: 14px;"><span style="font-family: 宋体;">具有等效连续噪声测量</span>Leq<span style="font-family: 宋体;">和总体</span>LEq9h (CR:310)</span></p><p style="white-space: normal; line-height: 3em;"><span style="font-size: 14px;">DC<span style="font-family: 宋体;">和</span>AC<span style="font-family: 宋体;">输出</span></span></p><p style="white-space: normal; line-height: 3em;"><span style="font-family: 宋体; font-size: 14px;">长久电池工作时间</span></p><p><br/></p>', '<p style="line-height: 2em;"><strong><span style="font-size: 14px;">声级计</span></strong></p><p style="line-height: 2em;"><strong><span style="font-size: 14px;">型号：CR:308/CR:310</span></strong></p><p style="line-height: 2em;"><span style="font-size: 14px;"><span style="font-size: 14px; font-family: 宋体;">最新推出的二级声级计</span>CR:308 &amp; CR:310<span style="font-size: 14px; font-family: 宋体;">用于基本噪声测量及检测的简单声级计。其应用广泛</span>,<span style=