INSERT INTO `8_wd_info` VALUES ('29', '../uploads/20161017/20161017103122_974.jpg', 'CameraLink 视频光端机', '<p style="text-align:left;line-height:25px;background:white"><span style="font-size:12px;font-family:宋体;color:#5E5F5E;background:white">天翼讯通自主研发的Base,Medium,Full CameraLink2.0全功能型的Camera Link光端机。采用不同的复用技术Camera Link2.0信号可以与其它多种视频、数据信号同时传输。普军级工作温度范围内，可配置高性能的数码相机和图像采集器，用于延长Camera-Link2.0接口图像数据的传输距离和应用于特殊的环境适用于各种工业监控控制、交通控制、军用高清图像传输等。天翼讯通WINGMAX研发生产的</span><span style="font-size:12px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#5E5F5E;background:white">Camera Link2.0</span><span style="font-size:12px;font-family:宋体;color:#5E5F5E;background:white">光端机由一台发射光端机和一台接收光端机组成，需两台产品配对使用。该产品能够实现</span><span style="font-size:12px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#5E5F5E;background:white">2</span><span style="font-size:12px;font-family:宋体;color:#5E5F5E;background:white">路</span><span style="font-size:12px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#5E5F5E;background:white">Full</span><span style="font-size:12px;font-family:宋体;color:#5E5F5E;background:white">模式</span><span style="font-size:12px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;;color:#5E5F5E;background:white">Camera Link2.0</span><span style="font-size: 12px;font-family:宋体;color:#5E5F5E;background:white">视频信号的实时、无压缩光纤远传功能，具有超小体积、超轻重量、功耗低、安装方便、操作简单、维修便利、可靠性高等特点。</span></p><p><br/></p>', '0', '2016-10-17', '288', '1');

