INSERT INTO `8_wd_news` VALUES ('1000064', '人员招聘', '', '', '', '', '', '1', '', '0', '0', '<p><span style=";font-family:宋体;font-size:14px"><span style="font-family:宋体">职位名称：团队合伙人</span></span></p><p><span style=";font-family:宋体;font-size:14px"><span style="font-family:宋体">招聘人数：</span>3<span style="font-family:宋体">人</span></span></p><p><span style=";font-family:宋体;font-size:14px"><span style="font-family:宋体">上班地址：总部天津（此岗位需要全国出差）</span></span></p><p><span style=";font-family:宋体;font-size:14px"><span style="font-family:宋体">工作年限：</span>3<span style="font-family:宋体">年以上团队管理工作经验</span></span></p><p><span style=";font-family:宋体;font-size:14px"><span style="font-family:宋体">工作性质：全职</span></span></p><p><span style=";font-family:宋体;font-size:14px"><span style="font-family:宋体">薪资：</span>1W<span style="font-family:宋体">——</span><span style="font-family:Calibri">3W</span></span></p><p><span style=";font-family:宋体;font-size:14px"><span style="font-family:宋体">福利：五险一金，管吃管住，出差补助，专业培训，十余项公司固定福利内容</span></span></p><p><span style=";font-family:宋体;font-size:14px"><span style="font-family:宋体">职能描述：</span></span></p><p><span style="font-family:宋体;font-size:14px">1、</span><span style=";font-family:宋体;font-size:14px"><span style="font-family:宋体">为管辖市场制定规划及日常管理、团队管理、专业培训、特色会议讲解；</span></span></p><p style="margin-left:0;text-indent:0"><span style="font-family:宋体;font-size:14px">2、</span><span style=";font-family:宋体;font-size:14px"><span style="font-family:宋体">帮助市场制定全年销售、消耗规划，完成年度业绩目标；</span></span></p><p style="margin-left:0;text-indent:0"><span style="font-family:宋体;font-size:14px">3、</span><span style=";font-family:宋体;font-size:14px"><span style="font-family:宋体">帮助团队成员制定月、季度、年度绩效考核计划。</span></span></p><p><span style=";font-family:宋体;font-size:14px"><span style="font-family:宋体">岗位工作职责：</span></span></p><p><span style=";font-family:宋体;font-size:14px">1<span style="font-family:宋体">、 女性：形象好、气质佳，身高</span><span style="font-family:Calibri">165cm</span><span style="font-family:宋体">以上；</span></span></p><p><span style=";font-family:宋体;font-size:14px">2<span style="font-family:宋体">、年龄在</span><span style="font-family:Calibri">25--40</span><span style="font-family:宋体">岁之间均可；</span></span></p><p><span style=";font-family:宋体;font-size:14px">3<span style="font-family:宋体">、普通话标准，沟通能力强、性格外向，吃苦耐劳，能适应出差工作；</span></span></p><p><span style=";font-family:宋体;font-size:14px">4<span style="font-family:宋体">、有</span><span style="font-family:Calibri">3</span><span style="font-family:宋体">年</span><span style="font-family:Calibri">--5</span><span style="font-family:宋体">年以上团队管理工作经验。</span></span></p><p><span style=";font-family:宋体;font-size:14px">&nbsp;</span></p><p><span style=";font-family:宋体;font-size:14px"><span style="font-family:宋体">职位名称：坐诊专家</span></span></p><p><span style=";font-family:宋体;font-size:14px"><span style="font-family:宋体">招聘人数：</span>5<span style="font-family:宋体">人</span></span></p><p><span style=";font-family:宋体;font-size:14px"><span style="font-family:宋体">上班地址：总部天津（此岗位需要全国出差）</span></span></p><p><span style=";font-family:宋体;font-size:14px"><span style="font-family:宋体">工作年限：</span>3<span style="font-family:宋体">年以上坐诊销售工作经验</span></span></p><p><span style=";font-family:宋体;font-size:14px"><span style="font-family:宋体">工作性质：全职</span></span></p><p><span style=";font-family:宋体;font-size:14px"><span style="font-family:宋体">薪资：</span>1W<span style="font-family:宋体">——</span><span style="font-family:Calibri">4W</span></span></p><p><span style=";font-family:宋体;font-size:14px"><span style="font-family:宋体">福利：五险一金，管吃管住，出差补助，专业培训，十余项公司固定福利内容</span></span></p><p><span style=";font-family:宋体;font-size:14px"><span style="font-family:宋体">职能描述：</span></span></p><p><span style="font-family:宋体;font-size:14px">1、</span><span style=";font-family:宋体;font-size:14px"><span style="font-family:宋体">服务领导安排大客户的服务任务，和代理商、店家配合完成大客户的销售工作；</span></span></p><p><span style="font-family:宋体;font-size:14px">2、</span><span style=";font-family:宋体;font-size:14px"><span style="font-family:宋体">配合公司安排完成各种会议的坐诊销售工作；</span></span></p><p><span style="font-family:宋体;font-size:14px">3、</span><span style=";font-family:宋体;font-size:14px"><span style="font-family:宋体">积极配合公司安排的技能培训提升的安排。</span></span></p><p><span style=";font-family:宋体;font-size:14px"><span style="font-family:宋体">岗位工作职责：</span></span></p><p><span style=";font-family:宋体;font-size:14px">1<span style="font-family:宋体">、 女性：形象好、气质佳，身高</span><span style="font-family:Calibri">160cm</span><span style="font-family:宋体">以上；</span></span></p><p><span style=";font-family:宋体;font-size:14px">2<span style="font-family:宋体">、年龄在</span><span style="font-family:Calibri">35--60</span><span style="font-family:宋体">岁之间均可；</span></span></p><p><span style=";font-family:宋体;font-size:14px">3<span style="font-family:宋体">、普通话标准，沟通能力强、性格外向，吃苦耐劳，能适应出差工作；</span></span></p><p><span style=";font-family:宋体;font-size:14px">4<span style="font-family:宋体">、有</span><span style="font-family:Calibri">3</span><span style="font-family:宋体">年</span><span style="font-family:Calibri">--5</span><span style="font-family:宋体">年以上优质销售工作经验。</span></span></p><p><span style=";font-family:宋体;font-size:14px">&nbsp;</span></p><p><span style=";font-family:宋体;font-size:14px"><span style="font-family:宋体">职位名称：市场导师</span></span></p><p><span style=";font-family:宋体;font-size:14px"><span style="font-family:宋体">招聘人数：</span>10<span style="font-family:宋体">人</span></span></p><p><span style=";font-family:宋体;font-size:14px"><span style="font-family:宋体">上班地址：总部天津（此岗位需要全国出差）</span></span></p><p><span style=";font-family:宋体;font-size:14px"><span style="font-family:宋体">工作年限：</span>1<span style="font-family:宋体">年以上带店培训、管理工作经验</span></span></p><p><span style=";font-family:宋体;font-size:14px"><span style="font-family:宋体">工作性质：全职</span></span></p><p><span style=";font-family:宋体;font-size:14px"><span style="font-family:宋体">薪资：</span>8K<span style="font-family:宋体">——</span><span style="font-family:Calibri">2W</span></span></p><p><span style=";font-family:宋体;font-size:14px"><span style="font-family:宋体">福利：五险一金，管吃管住，出差补助，专业培训，十余项公司固定福利内容</span></span></p><p><span style=";font-family:宋体;font-size:14px"><span style="font-family:宋体">职能描述：</span></span></p><p><span style=";font-family:宋体;font-size:14px">1<span style="font-family:宋体">、负责公司指定区域代理市场的售后服务支持</span></span></p><p><span style=";font-family:宋体;font-size:14px">2<span style="font-family:宋体">、针对每个市场情况结合总部支持，制定活动方案维护所辖区域市场的品牌运营。</span></span></p><p><span style=";font-family:宋体;font-size:14px">3<span style="font-family:宋体">、配合代理商市场针对已有会员进行产品销售</span></span></p><p><span style=";font-family:宋体;font-size:14px">4<span style="font-family:宋体">、负责所辖店面的各项教育培训，包含专业知识、产品技术、会议模式。</span></span></p><p><span style=";font-family:宋体;font-size:14px"><span style="font-family:宋体">岗位工作职责：</span></span></p><p><span style=";font-family:宋体;font-size:14px">1<span style="font-family:宋体">、 女性：形象好、气质佳，愿意接受有挑战、 高薪的工作。</span></span></p><p><span style=";font-family:宋体;font-size:14px">2<span style="font-family:宋体">、年龄在</span><span style="font-family:Calibri">22</span><span style="font-family:宋体">—</span><span style="font-family:Calibri">55</span><span style="font-family:宋体">岁之间均可。</span></span></p><p><span style=";font-family:宋体;font-size:14px">3<span style="font-family:宋体">、普通话标准，沟通能力强、性格外向，吃苦耐劳，能适应出差工作。</span></span></p><p><span style=";font-family:宋体;font-size:14px">4<span style="font-family:宋体">、有</span><span style="font-family:Calibri">1</span><span style="font-family:宋体">年以上相关工作经验，具备医学、护理、美容、市场营销等专业优先录取。</span></span></p><p><span style=";font-family:宋体;font-size:14px">&nbsp;</span></p><p><span style=";font-family:宋体;font-size:14px">&nbsp;</span></p><p><span style=";font-family:宋体;font-size:14px"><span style="font-family:宋体">以上岗位有意者请与公司人事联系</span></span></p><p><span style=";font-family:宋体;font-size:14px"><span style="font-family:宋体">联系人：潘女士</span></span></p><p><span style=";font-family:宋体;font-size:14px"><span style="font-family:宋体">电话：</span>18526792072<span style="font-family:宋体">（微信同号）</span></span></p><p><span style=";font-family:宋体;