INSERT INTO `8_wd_news` VALUES ('1000091', '2016', '', '', '', '', '', '1', '2016年艾可国际和中国第三大基金会“中国初级卫生保健基金会”', '8', '1', '<p><br/></p><p style="line-height: 1.75em;"><span style="font-size: 14px;">2016年艾可国际和中国第三大基金会“中国初级卫生保健基金会”在北京联合启动了女性生殖健康的公益工程“艾可女性1+1幸福工程”，期间艾可国际捐赠价值500万元产品以及捐赠公益巡诊车一辆，旨在帮助改善中国偏远、落后地区女性的基础卫生和基础医疗，同时普及女性生殖健康知识，为中国女性的幸福事业保驾护航。</span></p><p style="line-height: 1.75em;"><br/></p><p><br/></p><p><br/></p><p><br/></p>', '364', '2016', '', '', '', 'info', '', '', '0', '0', '', '', '1634881079', '0', '', '');
INSERT INTO `8_wd_news` VALUES ('1000092', '2020', '', '', '', '', '', '1', '2020', '10', '1', '<p style="line-height: 2em;"><span style="font-family: 微软雅黑, " microsoft="" font-size:=""><span style="font-family: 微软雅黑, " microsoft=""></span></span></p><p style="line-height: 1.75em;"><span style="font-size: 14px;">2020年新年伊始，一场新冠疫情突然爆发，席卷全国乃至全球。大灾大疫面前，艾可国际临危受命，响应天津市政府的号召，克服重重困难、第一时间全面复工复产，圆满完成了天津工信部等各级政府部门布置的“抗疫物资”生产任务，为抗灾抗疫贡献自己的力量，勇于担负起企业的社会责任和社会使命。</span></p><p style="line-height: 2em;"><br/></p>', '364', '2020', '', '', '', 'info', '', '', '0', '0', '', '', '1634881098', '0', '', '');
