INSERT INTO `8_wd_news` VALUES ('1000120', '天津艾可国际贸易有限公司', '', '../uploads/20211018/20211018101531_685.jpg', '', '', '', '1', '', '0', '0', '<p style="text-indent: 37px; line-height: 3em;"><span style="font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 18px;">2010年11月企业总裁白禹女士把American AFPC---“AER女性生殖健康管理机构”引入中国并与原国家计生委合作成立第一家“AER女性生殖健康管理中心”，从身、心、灵、性四个方面帮助女性获得健康、自信、幸福、快乐，彻底更新女性传统保养观念，独创女性全方位四维一体康复保养抗衰体系，真正有效呵护女性健康。</span></p><p style="text-indent: 37px; line-height: 3em;"><span style="font-size: 18px;"><span style="font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;">2013年率先在中国开展“AER性感训练营”迅速风靡全国，成为国际性感训练营的开创者。目前，艾可国际贸易公司旗下运营品牌有AER、FULOVE、HOOI等，拓展运营商覆盖国内</span><span style="font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;">18个省，4个直辖市，加盟单店规模已经突破900余家。</span></span></p><p><br/></p>', '384', '2021-10-18', '', '', '', 'info', '', '', '0', '0', '', '', '1634523331', '0', '', '');
