INSERT INTO `8_wd_news` VALUES ('1000105', '工厂OEM板块介绍', '', '../uploads/20210923/20210923114431_255.jpg', '', '', '', '1', '', '0', '0', '<p style="text-align: center;"><span style="font-size: 24px;">天津艾可科技发展有限公司</span></p><p style="text-align: center;"></p><p style="text-align: center;"><span style="font-size: 24px;"><br/></span></p><p style="text-align: center;"><span style="font-size: 24px;">品质高于一切&nbsp; &nbsp; &nbsp;安全有效第一</span></p><p style="text-align: center;"></p><p style="text-align: center;">是一家研发生产、包装、 OEM 、ODM一站式服务于一体的综合型企业。拥有研发实验室、检测室、生产车间、包装车间、物流仓储等九大部门体系化科学管理。&nbsp; &nbsp;&nbsp;</p><p style="text-align: center;">&nbsp; &nbsp;&nbsp;</p><p style="text-align: center;">艾可生殖健康产业基地的很多工艺水平达到行业内最高标准，生产的清洁用水采用二级ro膜反渗透纯化水处理系统，完全符合中国药典标准及相关法规标准，其中净化车间更是达到GMP万级标准高于法规规定的10万级，拥有多条全自动灌装生产线，能够完成日产量百万订单。产品系列分为：凝胶类、洗液类</p><p><br/></p><p style="text-align: center;">渠&nbsp; 道：专业院线 日化线&nbsp; 新媒体直播 微商、电商、OEM代工</p><p><br/></p><p><span style="font-size: 24px;"><br/></span></p><p style="text-align: center;"><img src="/kb_008/ueditor/ueditor/php/upload/image/20210923/1632368496412588.png" title="1632368496412588.png" alt="image.png"/>&nbsp;&nbsp;</p>', '381', '2021-09-24', '', '', '', 'info', '', '', '0', '0', '', '', '1632368671', '0', '', '');
