INSERT INTO `8_wd_news` VALUES ('1000107', '2015', '', '', '', '', '', '1', '2015年艾可国际在北京主持并召开了首届“殖美抗衰国际高峰论坛”', '7', '1', '<p style="line-height: 2em;"><span style="font-size: 14px;">2015年艾可国际在北京主持并召开了首届“殖美抗衰国际高峰论坛”，并与中华工商业联合会美容化妆品业商会共同成立了“中华工商业联合会美容化妆品业商会殖美抗衰委员会”，其职责范围包含：殖美行业的行标制定、规范文件的起草、引领行业发展。</span></p><p style="line-height: 2em;"><span style="font-size: 14px;">自2015年成立以来，艾可国际始终坚持做行业表率，紧密团结在商会领导下，积极开拓殖美行业规范发展，先后启动了500余场“殖美规范教育训练营”、草拟并提交了殖美师操作规范系列培训指南；为行业科学、积极发展，注入了极大的生命力。</span></p><p><br/></p>', '364', '2015', '', '', '', 'info', '', '', '0', '0', '', '', '1634881027', '0', '', '');
