INSERT INTO `en_archives` VALUES ('501', 'Longen 2017 annual meeting', '', 'Time flies,  one year goes by.We said goodbye to the successful year of 2017 and ushered in the promising year of 2018.', '0', '', '', '', '', '', '20210926\0283636442d78fe0b765c2960a0f6729.jpg', '', '22', '<p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; text-align: center;" microsoft="" letter-spacing:="" white-space:="" color:="" text-indent:="" line-height:=""><span style="color: rgb(0, 0, 0); font-size: 16px;"></span></p><p style="text-align: center;">&nbsp;</p><p style="text-align: center;">&nbsp;&nbsp;&nbsp;&nbsp;<table data-sort="sortEnabled" class="sortEnabled"><tbody><tr class="firstRow"><td width="12" valign="top"><br/></td><td valign="bottom" rowspan="1" colspan="1" style="word-break: break-all;" align="left" width="947"><p style="margin-top: 0px; margin-bottom: 0px; white-space: normal; padding: 0px; line-height: 3em; text-indent: 0em;"><span style="color: rgb(89, 89, 89);"><span style="font-size: 16px;">&nbsp;</span></span></p><p style="text-align:center"><img src="/lakj/kbcms/ueditor/php/upload/image/20210926/1632642207207972.png" title="1632642207207972.png" alt="蓝线.png" style="text-align: center;"/></p><p style="text-align:center"><span style="font-size: 16px; color: rgb(89, 89, 89); text-indent: 2em;">&nbsp;</span><br/></p><p style="margin-top:0;margin-right:0;margin-bottom:0;margin-left:0;text-indent:28px;padding:0 0 0 0 ;line-height:42px"><span style="font-family: 微软雅黑;color: rgb(63, 63, 63);letter-spacing: 0;font-size: 16px">Warmly celebrated on Longen 2017 annual meeting.</span></p><p style="margin-top:0;margin-right:0;margin-bottom:0;margin-left:0;text-indent:28px;padding:0 0 0 0 ;line-height:42px"><span style="font-family: 微软雅黑;color: rgb(63, 63, 63);letter-spacing: 0;font-size: 16px">Time flies, &nbsp;one year goes by.We said goodbye to the successful year of 2017 and ushered in the promising&nbsp;</span></p><p style="margin: 0px; padding: 0px; line-height: 42px; text-indent: 0em;"><span style="font-family: 微软雅黑;color: rgb(63, 63, 63);letter-spacing: 0;font-size: 16px">year of 2018. Longen take New Year&#39;s greeting to all our customers in advance. Thank you for your support in&nbsp;</span></p><p style="margin: 0px; padding: 0px; line-height: 42px; text-indent: 0em;"><span style="font-family: 微软雅黑;color: rgb(63, 63, 63);letter-spacing: 0;font-size: 16px">the past few years. We will keep better in next few years.</span></p><p style="margin-top:0;margin-right:0;margin-bottom:0;margin-left:0;text-indent:28px;padding:0 0 0 0 ;line-height:42px"><span style="font-family: 微软雅黑;color: rgb(63, 63, 63);letter-spacing: 0;font-size: 16px">Looking back to 2017, in a right direction of Longen&#39;s lead team, all employees struggle together . We&nbsp;</span></p><p style="margin: 0px; padding: 0px; line-height: 42px; text-indent: 0em;"><span style="font-family: 微软雅黑;color: rgb(63, 63, 63);letter-spacing: 0;font-size: 16px">achieved good efforts by strengthening team building, good &nbsp;marketing, carfully safety and quality control .&nbsp;</span></p><p style="margin: 0px; padding: 0px; line-height: 42px; text-indent: 0em;"><span style="font-family: 微软雅黑;color: rgb(63, 63, 63);letter-spacing: 0;font-size: 16px">We are satisfied with our sales growth in 2017 and we believe we can do better in next year.Because Longen&nbsp;</span></p><p style="margin: 0px; padding: 0px; line-height: 42px; text-indent: 0em;"><span style="font-family: 微软雅黑;color: rgb(63, 63, 63);letter-spacing: 0;font-size: 16px">is always keep moving.</span></p><p style="margin-top:0;margin-right:0;margin-bottom:0;margin-left:0;text-indent:28px;padding:0 0 0 0 ;line-height:42px"><span style="font-family: 微软雅黑;color: rgb(63, 63, 63);letter-spacing: 0;font-size: 16px">Looking forward to 2018, innovation is the key point in company competition.How to improve Lonen&#39;s&nbsp;</span></p><p style="margin: 0px; padding: 0px; line-height: 42px; text-indent: 0em;"><span style="font-family: 微软雅黑;color: rgb(63, 63, 63);letter-spacing: 0;font-size: 16px">technology is our first target.We will keep technology innovation to improve company&#39;s competition.</span></p><p style="margin-top: 0px; margin-bottom: 0px; white-space: normal; padding: 0px; text-indent: 2em; line-height: 3em;"><span style="color: rgb(89, 89, 89);"><span style="font-size: 16px; text-indent: 0em;"><br/></span></span></p><p style="margin-top: 0px; margin-bottom: 0px; white-space: normal; padding: 0px; text-indent: 2em; line-height: 3em;"><span style="color: rgb(89, 89, 89);"><span style="font-size: 16px; text-indent: 0em;">&nbsp; &nbsp; &nbsp; &nbsp;<img src="http://wangchao.a.kbyun.com/lakj/kbcms/ueditor/php/upload/image/20210926/1632636341961373.jpg" title="1632636341961373.jpg" alt="1632636341961373.jpg" width="750" height="443" border="0" vspace="0" style="font-size: 16px; text-align: center; white-space: normal; width: 750px; height: 443px;"/>&nbsp; &nbsp; &nbsp; &nbsp;</span></span></p><br/></td><td width="5" valign="top"><br/></td></tr></tbody></table><br/></p><p><br/></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em; line-height: 3em; text-align: center;"><span style="color: rgb(89, 89, 89);"><span style="color: rgb(89, 89, 89); font-size: 16px; text-indent: 0em;"><br/><br/></span></span></p><p style="text-align: center; text-indent: 0em; line-height: 3em;"><br/></p><p style="text-indent: 0em; line-height: 3em; text-align: center;"><span style="color: rgb(0, 0, 0); font-size: 16px; text-indent: 0em;"><br/></span></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; text-indent: 2em; line-height: 3em;"><br/></p><p><br/></p>', '373', '', '66', '1514822400', '999', '1', '0', '', '', '0', '', '', '', '', '', '', '', '', '', '', 'NULL', 'NULL', 'NULL', 'NULL', 'NULL', 'NULL', 'NULL', 'NULL', 'NULL', 'NULL', 'NULL', 'NULL', 'NULL', 'NULL', 'NULL', 'NULL', 'NULL', 'NULL', 'NULL');
