INSERT INTO `th_news` VALUES ('721', 'Engine desorption test system', '', '../uploads/20170502/20170502155330_442.png', '', '0', '', '5', '0', '<p><strong><span style="font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;">1、 Product name</span></strong></p><p><span style="font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;"><img src="/ueditor/php/upload/image/20170502/1493711421829480.png" title="1493711421829480.png" alt="微信图片_20170502155253.png" width="163" height="218" style="width: 163px; height: 218px;"/></span></p><p><span style="font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;">&nbsp; &nbsp; Engine desorption test system</span></p><p><strong><span style="font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;">2、 purpose</span></strong></p><p><span style="font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;"><span style="font-family: Wingdings;">l</span> &nbsp;In order to meet the requirements of national environmental protection standard GB (18352.3-2005) and production consistency,</span></p><p><span style="font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;">Desorption test of vehicle fuel supply system</span></p><p><strong><span style="font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;">3, main functions and characteristics</span></strong></p><p><span style="font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;"><span style="font-family: Wingdings;">l</span> &nbsp;pressure/Automatic temperature compensation function</span></p><p><span style="font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;"><span style="font-family: Wingdings;">l</span>&nbsp;The vacuum pressure of the carbon tank can be adjusted automatically to simulate the working condition completely.</span></p><p><span style="font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;"><span style="font-family: Wingdings;">l</span> &nbsp;Line sampling: record the pressure / flow curve of the engine at different speed conditions.</span></p><p><span style="font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;">Make on-line detection and determination standard, which is used when the equipment is used for the first time and regular inspection.</span></p><p><span style="font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;"><span style="font-family: Wingdings;">l</span> &nbsp;Offline sampling inspection: regular sampling inspection, setting parameters match the current car body condition.</span></p><p><span style="font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;"><span style="font-family: Wingdings;">l</span> &nbsp;Online detection: the device can simulate the negative pressure generated by the engine and detect the flow characteristics of the desorption system.</span></p><p><strong><span style="font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;">4, main technical parameters</span></strong></p><p><span style="font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;"><span style="font-family: Wingdings;">l</span> &nbsp;Mass flowmeter: range 10L/min, precision + 0.5% F.S.</span></p><p><span style="font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;"><span style="font-family: Wingdings;">l</span>&nbsp;Pressure sensor: range 0 ~ -20.0kPa, + 0.5% F.S</span></p><p><span style="font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;"><span style="font-family: Wingdings;">l</span>&nbsp; Temperature sensor: range 0 ~ 50.0kPa, + 0.5 degrees centigrade.</span></p><p><strong><span style="font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;">5, test tempo:</span></strong></p><p><span style="font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;">1.0min/ piece</span></p><p style="margin-left: 100px"><br/></p><p><br/></p>', '0', '425', '2017-03-05 00:00', '', '', '', 'info', '', '', '0', '433', '', '', '1534474208', '0', '', '', '', '1', '', '', '', '');
