INSERT INTO `tp_archives` VALUES ('414', '诚挚与您相约AMTS 2017第十三届上海国际汽车制造技术与装备及材料展览会', '', '我司将在AMTS 2017第十三届上海国际汽车制造技术与装备及材料展览会（2017.9.5~2017.9.8）上参展我们的装备及材料。', '0', '', '', '', '', '', '20210926\5e6a83e707ab4a9b2b9fec08091a4961.jpg', '', '59', '<table data-sort="sortDisabled" align="center"><tbody><tr class="firstRow"><td valign="bottom" style="border-color: rgb(221, 221, 221);" align="left"><br/></td><td valign="bottom" rowspan="1" colspan="1" style="word-break: break-all; border-color: rgb(221, 221, 221);" align="left"><p><img src="/lakj/kbcms/ueditor/php/upload/image/20210926/1632646575692037.png" title="1632646575692037.png" alt="蓝线.png"/></p><p><br/></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(102, 102, 102); line-height: 3em;"><span style="font-size: 16px;">&nbsp; &nbsp; &nbsp; &nbsp;我司将在<strong>AMTS</strong>&nbsp;2017第十三届上海国际汽车制造技术与装备及材料展览会（2017.9.5~2017.9.8）上参展我们的装</span></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(102, 102, 102); line-height: 3em;"><span style="font-size: 16px;">备</span><span style="font-size: 16px;">及</span><span style="font-size: 16px;">材料。上海国际汽车制造技术与装备及材料展览会作为亚洲最大的汽车材料与设计、工艺与装备、质量与装配、工程</span></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(102, 102, 102); line-height: 3em;"><span style="font-size: 16px;">与</span><span style="font-size: 16px;">服务</span><span style="font-size: 16px;">技术行业盛会，上海国际汽车制造技术与装备及材料展览会（</span><strong microsoft="" font-size:="" text-indent:="" style="font-size: 16px;">AMTS</strong><span style="font-size: 16px;">）自2004年创办以来，以国际化的视角呈现汽</span></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(102, 102, 102); line-height: 3em;"><span style="font-size: 16px;">车</span><span style="font-size: 16px;">工程领</span><span style="font-size: 16px;">域全方位的系统集成解决方案。10多年来，展会的规模、展示范围以及专业观众快速增长，极大地推动了国际汽</span></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(102, 102, 102); line-height: 3em;"><span style="font-size: 16px;">车</span><span style="font-size: 16px;">制造装备</span><span style="font-size: 16px;">技术交流及贸易的发展！</span></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(102, 102, 102); line-height: 3em;"><span style="font-size: 16px;"><strong>&nbsp; &nbsp; &nbsp; &nbsp;AMTS</strong>&nbsp;2017第十三届上海国际汽车制造技术与装备及材料展览会将于2017年9月5-8日在上海新国际博览中心举行，</span></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(102, 102, 102); line-height: 3em;"><span style="font-size: 16px;">将</span><span style="font-size: 16px;">汇聚来自中国/亚洲的知名汽车制造商和一级供应商的工程技术人员和决策者到场参观，预计将有来自世界各地800家展</span></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(102, 102, 102); line-height: 3em;"><span style="font-size: 16px;">商参</span><span style="font-size: 16px;">展，展示面积首次突破80,000平方米，吸引80,000专业观众到场参观。</span></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(102, 102, 102); line-height: 3em;"><span style="font-size: 16px;"><br/></span></p><p style="text-align:center"><span style="font-family: 微软雅黑, " microsoft="" font-size:=""><img src="/lakj/kbcms/ueditor/php/upload/image/20210926/1632646674369255.png" title="1632646674369255.png" alt="1501206324395031.png"/></span></p><p><span style="font-family: 微软雅黑, " microsoft="" font-size:=""><br/></span></p><p style="line-height: 3em;"><span style="font-size: 16px;">&nbsp; &nbsp; &nbsp; <span style="color: rgb(102, 102, 102);" microsoft="" font-size:="" text-indent:="">我司届时通过展示汽车核心部件成形工程、冷热处理到打磨抛光修边、质量控制及检测、连接、组装及测试、部件清洁</span></span></p><p style="line-height: 3em;"><span style="font-size: 16px;"><span style="color: rgb(102, 102, 102);" microsoft="" font-size:="" text-indent:="">及标</span></span><span style="color: rgb(102, 102, 102); font-size: 16px;">识、数控夹具到自动上下料机器人系统应用等，提供满足从单件加工到大批量生产，以及全自动整体解决方案，功能测</span></p><p style="line-height: 3em;"><span style="color: rgb(102, 102, 102); font-size: 16px;">试</span><span style="color: rgb(102, 102, 102); font-size: 16px;">用快速</span>