INSERT INTO `tp_archives` VALUES ('500', '热烈欢迎北京奔驰客人来我司进行项目验收', '', '2017年9月13日，北京奔驰与蒂森克虏伯来我司进行了项目验收', '0', '', '', '', '', '', '20210926\4105d1267fc4b91c62566bd7646a4a99.jpg', '', '36', '<table data-sort="sortDisabled" style="width: 768px;" align="center"><tbody><tr class="firstRow"><td width="248" valign="top"><br/></td><td valign="top" rowspan="1" colspan="1" style="word-break: break-all;"><p><img src="/lakj/kbcms/ueditor/php/upload/image/20210926/1632641914123466.png" title="1632641914123466.png" alt="蓝线.png"/></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; white-space: normal; color: rgb(132, 132, 132); font-family: Arial; line-height: 24px; text-indent: 2em; background-color: rgb(255, 255, 255);"><span style="line-height: 28px; font-size: 16px; font-family: arial, helvetica, sans-serif; color: rgb(89, 89, 89);"><br/></span></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; white-space: normal; color: rgb(132, 132, 132); font-family: Arial; background-color: rgb(255, 255, 255); text-indent: 0em; line-height: 3em;"><span style="line-height: 28px; font-size: 16px; font-family: arial, helvetica, sans-serif; color: rgb(89, 89, 89);">&nbsp; &nbsp; &nbsp; &nbsp; 热烈欢迎北京奔驰与蒂森克虏伯一行来我司进行项目验收。</span></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; white-space: normal; color: rgb(132, 132, 132); font-family: Arial; background-color: rgb(255, 255, 255); text-indent: 0em; line-height: 3em;"><span style="font-size: 16px; text-indent: 2em; font-family: arial, helvetica, sans-serif; color: rgb(89, 89, 89);">&nbsp; &nbsp; &nbsp; &nbsp; 2017年9月13日，北京奔驰与蒂森克虏伯来我司进行了项目验收。与会专家听取了项目汇报、审查了文件资料、</span><span style="color: rgb(89, 89, 89); font-family: arial, helvetica, sans-serif; font-size: 16px; text-indent: 2em;">察看了生产现场，验收工作顺利完成。</span></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; white-space: normal; color: rgb(132, 132, 132); font-family: Arial; background-color: rgb(255, 255, 255); text-indent: 0em; line-height: 3em