INSERT INTO `tp_cate` VALUES ('364', '公司简介', 'COMPANY PROFILE', 'cn', '', '', '', '<p><img src="/lakj/kbcms/ueditor/php/upload/image/20210921/1632205158125209.jpg" alt="" style="vertical-align: middle; border: none; width: 535px; height: 352px; overflow: hidden; float: left;margin-right: 20px"/></p><p style="text-align: justify; line-height: 4em;"><span style="font-size: 16px;">&nbsp; &nbsp; &nbsp;</span></p><p style="text-align: justify; text-indent: 2em; line-height: 3em;"><span style="font-size: 16px;">&nbsp; &nbsp; 朗安（天津）科技发展有限公司成立于 2011年9月30日，</span><span style="font-size: 16px;">位于中国</span><span style="font-size: 16px; text-indent: 0em;">天津</span></p><p style="text-align: justify; line-height: 3em;"><span style="font-size: 16px; text-indent: 0em;">&nbsp; &nbsp;市东丽区华明高新区，是国家级高新技术企业，通</span><span style="font-size: 16px; text-indent: 0em;">过了</span><span style="font-size: 16px;">ISO9001认</span><span style="font-size: 16px;">证，拥有</span></p><p style="text-align: justify; line-height: 3em;"><span style="font-size: 16px;">&nbsp; &nbsp;多项发明专利和实用新型专利。</span><span style="font-size: 16px;">公司成立以来，产品已经应用</span><span style="font-size: 16px;">在奔驰、 沃尔</span></p><p style="text-align: justify; line-height: 3em;"><span style="font-size: 16px;">&nbsp; &nbsp;沃、</span><span style="font-size: 16px;">一汽、上汽、</span><span style="font-size: 16px;">广汽、长城、吉利、比亚迪、博世、</span><span style="font-size: 16px;">爱信</span><span style="font-size: 16px;">等众多知名工厂，</span></p><p style="text-align: justify; line-height: 3em;"><span style="font-size: 16px;">&nbsp; &nbsp;并</span><span style="font-size: 16px;">已</span><span style="font-size: 16px;">远销到德国、瑞</span><span style="font-size: 16px;">典、意大利、</span><span style="font-size: 16px;">&nbsp; &nbsp;西班牙、</span><span style="font-size: 16px;">巴西、</span><span style="font-size: 16px;"></span><span style="font-size: 16px;">印度、越南等国家。</span></p><p dir="ltr" style="text-align: justify; text-indent: 2em; line-height: 3em;"><span style="font-size: 16px;">&nbsp; &nbsp; &nbsp;朗安公司以持续引领创新，制造卓越产品为己任，坚持不</span><span style="font-size: 16px;">懈提高</span><span style="font-size: 16px;">产品</span><span style="font-size: 16px;">设</span></p><p dir="ltr" style="text-align: justify; text-indent: 0em; line-height: 3em;"><span style="text-indent: 2em; font-size: 16px;">&nbsp; &nbsp;计水平，优化生产工艺流程，为客户提供更优质可</span><span style="text-indent: 2em; font-size: 16px;">靠的产品和解决方案。</span></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: arial, " microsoft="" font-size:="" letter-spacing:="" white-space:="" background-color:="" text-indent:="" line-height:=""><span style="background-color: rgb(242, 242, 242); font-size: 20px;"></span></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: arial, " microsoft="" font-size:="" letter-spacing:="" white-space:="" background-color:="" text-indent:="" line-height:=""><span style="font-size: 20px;"></span></p><p style="margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(51, 51, 51);" microsoft="" font-size:="" letter-spacing:="" white-space:="" background-color:="" text-indent:="" line-height:=""><span style="font-size: 18px;"></span></p>', '0', '1', '1', '', '', '2', 'about.html', 'about.html', 'about.html', '1', '1000', '', '67', '363', '0', '', 'fa-th-list');
