INSERT INTO `wd_news` VALUES ('782', '朗安科技参展2020印度金奈汽车测试及质量监控展览会圆满成功', '', '../uploads/20200224/20200224151023_355.jpg', '', '1', '', '5', '1', '<p style="text-indent: 2em; line-height: 3em;"><span style="color: rgb(89, 89, 89); font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;">我司参展2020年印度金奈汽车测试及质量监控展览会Automotive Testing Expo于1月24日完美落幕。该次展会是印度南部最大的车辆和零部件测试与验证技术与服务展览，展会汇聚了世界领先的测试设备供应商和测试服务供应商，是汽车安全测试领域万众瞩目的一场盛会。</span></p><p style="text-indent: 2em; text-align: center; line-height: 3em;"><img src="/ueditor/php/upload/image/20200224/1582528005138515.jpg" title="1582528005138515.jpg" alt="未标题-1.jpg"/></p><p style="text-indent: 2em; line-height: 3em;"><span style="color: rgb(89, 89, 89); font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;">朗安科技此次参会，以介绍可提供精确解决方案的测试快速连接器及测试线束为重点。展会期间，朗安科技以一贯的真诚迎接来自世界各地的客户，共同讨论了汽车测试用快速连接器，并得到客户赞许和认可。</span></p><p style="text-indent: 2em; line-height: 3em;"><span style="color: rgb(89, 89, 89); font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;"></span></p><p style="margin-bottom: 0px; text-indent: 32px; line-height: 3em;"><span style="color: rgb(89, 89, 89); font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;">朗安科技在现场演示了不同型号快速连接器的应用，观看的客户都表示出了兴趣。朗安科技的应用领域涵盖了：功能测试所用到的快速连接器；发动机总成油道和水道气密测试解决方案；燃油系统气密性及通气性检测方案；汽车零部件气密检测；胶管、燃气行业以及医疗器械等领域的气密性检测解决方案。</span></p><p style="text-indent: 2em; text-align: center; line-height: 3em;"><img src="/ueditor/php/upload/image/20200224/1582528020100259.jpg" title="1582528020100259.jpg" alt="未标题-2.jpg"/></p><p style="text-indent: 2em; line-height: 3em;"><span style="color: rgb(89, 89, 89); font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;">本司通过该次展会这样的平台，加强与客户间的技术交流及商业合作，同时紧跟全球市场，了解新趋势，以不断提升技术和服务能力，扩大朗安在全球汽车安全测试领域的影响力。<br/></span></p><p style="text-indent: 2em; text-align: center; line-height: 3em;"><img src="/ueditor/php/upload/image/20200224/1582528031624864.jpg" title="1582528031624864.jpg" alt="未标题-3.jpg"/></p><p><br/></p>', '0', '371', '2020-01-24 22:45', '', '', '', 'info', '', '', '0', '7198', '', '', '1583120225', '0', '', '', '', '1', '', '', '', '');
