INSERT INTO `wd_news` VALUES ('649', '检漏仪', '', '../uploads/20161031/20161031113802_583.jpg', '../uploads/20161031/20161031113802_435.jpg', '0', '', '89', '0', '<p style="padding: 15px 0px; color: rgb(51, 51, 51); line-height: normal; font-family: arial, " microsoft=""><span style="font-family: 微软雅黑, " microsoft=""><strong>概述：</strong></span></p><p style="padding: 0px; color: rgb(102, 102, 102); line-height: normal; font-family: arial, " microsoft=""><img style="border: currentColor; vertical-align: middle;" src="/ueditor/php/upload/image/20161031/1477885061131462.jpg"/></p><p style="padding: 0px; color: rgb(102, 102, 102); line-height: normal; font-family: arial, " microsoft=""><span style="font-family: 微软雅黑, " microsoft="">“快速连接器”是一个广泛的工业产品配件名词，分为液压管道快速连接器和挖掘机 快速连接器。这里是指挖掘机快速连接器（也叫快换接头、快速连接头），快速连接器能 在挖掘机上迅速安装各种配置件(挖斗、松土器、破碎锤、液压剪等)，能扩大挖掘机使用 范围，并能节省时间，提高工效。</span></p><p style="padding: 15px 0px; color: rgb(51, 51, 51); line-height: normal; font-family: arial, " microsoft=""><span style="font-family: 微软雅黑, " microsoft=""><strong>产品参数：</strong></span></p><p style="padding: 0px; color: rgb(102, 102, 102); line-height: normal; font-family: arial, " microsoft=""><span style="font-family: 微软雅黑, " microsoft="">朗安快速连接器</span></p><p style="padding: 0px; color: rgb(102, 102, 102); line-height: normal; font-family: arial, " microsoft=""><span style="font-family: 微软雅黑, " microsoft="">含有蜂胶提取物、薰衣草精油</span></p><p style="padding: 0px; color: rgb(102, 102, 102); line-height: normal; font-family: arial, " microsoft=""><span style="font-family: 微软雅黑, " microsoft="">人造丝+含棉量70%</span></p><p style="padding: 0px; color: rgb(102, 102, 102); line-height: normal; font-family: arial, " microsoft=""><span style="font-family: 微软雅黑, " microsoft="">规格：200X160mm</span></p><p style="padding: 0px; color: rgb(102, 102, 102); line-height: normal; font-family: arial, " microsoft=""><span style="font-family: 微软雅黑, " microsoft="">质量：65gsm</span></p><p style="padding: 15px 0px; color: rgb(51, 51, 51); lin