INSERT INTO `wd_news` VALUES ('764', '挑战自我 团队协作 凝聚力量 共创佳绩——朗安科技2018职工拓展训练圆满成功', '', '../uploads/20180605/20180605094136_239.jpg', '', '1', '', '0', '1', '<p style="white-space: normal; text-indent: 2em; text-align: left;"><span style="background-color: rgb(255, 255, 255); color: rgb(98, 98, 98); letter-spacing: 0px; text-indent: 2em; font-size: 16px; font-family: 微软雅黑, " microsoft="">为加强公司员工队伍建设，增进员工间的相互交流，培养全体员工的团队精神和协作意识，公司人力资源部于2018年5月26-27日组织员工在北京金海湖、石林峡开展拓展训练活动，取得良好效果。</span></p><p style="text-align: center;"><img width="650" height="488" title="1528162685136410.jpg" style="width: 650px; height: 488px;" alt="1528162685136410.jpg" src="/ueditor/php/upload/image/20180605/1528162685136410.jpg" border="0" vspace="0"/></p><p><br/></p><p style="text-align: center;"><img width="650" height="488" title="1528162685115058.jpg" style="width: 650px; height: 488px;" alt="1528162685115058.jpg" src="/ueditor/php/upload/image/20180605/1528162685115058.jpg" border="0" vspace="0"/></p><p><br/></p><p style="text-align: center;"><img width="650" height="488" title="1528162685138101.jpg" style="width: 650px; height: 488px;" alt="1528162685138101.jpg" src="/ueditor/php/upload/image/20180605/1528162685138101.jpg" border="0" vspace="0"/></p><p><br/></p><p style="text-align: center;"><img width="650" height="488" title="1528162710283579.jpg" style="width: 650px; height: 488px;" alt="1528162710283579.jpg" src="/ueditor/php/upload/image/20180605/1528162710283579.jpg" border="0" vspace="0"/></p><p><br/></p><p style="text-align: center;"><img width="650" height="488" title="1528162685137701.jpg" style="width: 650px; height: 488px;" alt="1528162685137701.jpg" src="/ueditor/php/upload/image/20180605/1528162685137701.jpg" border="0" vspace="0"/></p><p style="white-space: normal; text-indent: 2em; text-align: left;"><span style="background-color: rgb(255, 255, 255); color: rgb(98, 98, 98); letter-spacing: 0px; text-indent: 2em; font-size: 16px; font-family: 微软雅黑, " microsoft=""><br/></span></p><p style="white-space: normal; text-indent: 2em; text-align: left;"><span style="background-color: rgb(255, 255, 255); color: rgb(98, 98, 98); letter-spacing: 0px; text-indent: 2em; font-size: 16px; font-family: 微软雅黑, " microsoft="">在拓展训练项目中，一个个精心设计的拓展项目激发了大家浓厚的参与兴趣与热情。面对着一个个充满挑战的项目，各队队员集思广益，团结协作，用思考、汗水在同心协力中成功完成挑战，共同分享了成功的喜悦和挑战的激情。大家积极参与，迎难而上，大胆挑战自我，队员之间相互鼓励，不逃避不放弃，所有团队均圆满完成了各拓展项目的培训。在训练过程中，团队成员们充分地体会到了团队信任、有效沟通、换位思考、合理组织、领导力、执行力和团队协作等和工作息息相关的管理意识和艺术。</span></p><p style="white-space: normal; text-indent: 2em; text-align: center;"><img width="650" height="488" title="1528162749124947.jpg" style="color: rgb(98, 98, 98); letter-spacing: 0px; text-indent: 2em; width: 650px; height: 488px;" alt="1528162749124947.jpg" src="/ueditor/php/upload/image/20180605/1528162749124947.jpg" border="0" vspace="0"/></p><p style="white-space: normal; text-indent: 2em; text-align: left;"><br/><span style="background-color: rgb(255, 255, 255); color: rgb(98, 98, 98); letter-spacing: 0px; text-indent: 2em; font-size: 16px; font-family: 微软雅黑, " microsoft="">&nbsp;&nbsp;&nbsp;此次活动有效增强了参训员工的自信心和沟通力，增进了员工的团结协作意识。通过相互间的交流和学习，使大家认识到了彼此信任、团队合作和善于创新的重要性，对提高工作效率、增进各部门沟通协调以及提高执行力等起到积极的作用。</span></p>', '0', '358', '2018-06-05', '', '', '', 'info', '', '', '0', '6876', '', '', '1546579169', '0', '', '', '', '1', '', '', '', '');
INSERT INTO `wd_news` VALUES ('714', 'LA800型快速连接器', '', '../uploads/20210425/20210425163642_578.jpg', '../uploads/20210723/20210723113031_183.pdf', '1', '', '84', '0', '<p style="text-align: center;"><img src="/ueditor/php/upload/image/20210723/1627011020307978.jpg" title="1627011020307978.jpg" alt="朗安科技-快速连接器选型手册_页面_46.jpg"/></p><p style="text-align: center;"><img src="/ueditor/php/upload/image/20210427/1619503290340252.gif" title="1619503290340252.gif" alt="LA800.gif" width="547" height="284" style="width: 547px; height: 284px;"/><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p>', '0', '424', '2017-04-15 00:00', '', '', '', 'info', '', '', '0', '10724', '', '', '1627011031', '0', '', '', '', '1', '', 'LA800型快速连接器资料', '', '');
