INSERT INTO `wd_news` VALUES ('721', '发动机脱附测试系统', '', '../uploads/20170502/20170502155330_442.png', '', '0', '', '5', '0', '<p><strong><span style="font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;">1、 产品名称</span></strong></p><p><span style="font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;"><img src="/ueditor/php/upload/image/20170502/1493711421829480.png" title="1493711421829480.png" alt="微信图片_20170502155253.png" width="163" height="218" style="width: 163px; height: 218px;"/></span></p><p><span style="font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;">&nbsp; &nbsp; 发动机脱附测试系统</span></p><p><strong><span style="font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;">2、 用途</span></strong></p><p><span style="font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;"><span style="font-family: Wingdings;">l</span> &nbsp;为保证达到国家环保有关标准GB（18352.3-2005）及生产一致性要求，</span></p><p><span style="font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;">对整车燃油供给系统进行脱附性试验</span></p><p><strong><span style="font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;">3、主要功能和特点</span></strong></p><p><span style="font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;"><span style="font-family: Wingdings;">l</span> &nbsp;压力/温度自动补偿功能</span></p><p><span style="font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;"><span style="font-family: Wingdings;">l</span>&nbsp;碳罐脱附口真空压力自动调整，可以完全模拟工况状态</span></p><p><span style="font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;"><span style="font-family: Wingdings;">l</span> &nbsp;线下采样：通过记录发动机在不同转速条件下对应的压力/流量曲线，</span></p><p><span style="font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;">做出在线检测和判定标准，该功能是设备初次使用和定期检查时使用</span></p><p><span style="font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;"><span style="font-family: Wingdings;">l</span> &nbsp;线下抽检：定期抽样检查，设定参数是否与当前车体状况匹配</span></p><p><span style="font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;"><span style="font-family: Wingdings;">l</span> &nbsp;在线检测：设备能够模拟发动机产生负压，检测脱附系统的流量特性</span></p><p><strong><span style="font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;">4、主要技术参数</span></strong></p><p><span style="font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;"><span style="font-family: Wingdings;">l</span> &nbsp;质量流量计：量程10L/min,精度±0.5% F.S.</span></p><p><span style="font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;"><span style="font-family: Wingdings;">l</span>&nbsp; 压力传感器：量程0～-20.0kPa，±0.5 % F.S</span></p><p><span style="font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;"><span style="font-family: Wingdings;">l</span>&nbsp; 温度传感器：量程0～50.0kPa，±0.5 ℃</span></p><p><strong><span style="font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;">5、测试节拍：</span></strong></p><p><span style="font-family: 微软雅黑, &#39;Microsoft YaHei&#39;; font-size: 14px;">1.0min/件</span></p><p style="margin-left: 100px"><br/></p><p><br/></p>', '0', '425', '2017-03-05 00:00', '', '', '', 'info', '', '', '0', '636', '', '', '1508731057', '0', '', '', '', '1', '', '', '', '');
