, '0', '5144', '', '', '1514859514', '0', '', '', '', '1', '', '', '', '');
INSERT INTO `wd_news` VALUES ('737', '诚挚与您相约AMTS 2017第十三届上海国际汽车制造技术与装备及材料展览会', '朗安科技', '../uploads/20170728/20170728094306_552.png', '', '1', '', '3', '0', '<p style="text-align: left; text-indent: 2em; line-height: 3em;"><span style="font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;">我司将在<strong>AMTS</strong> 2017第十三届上海国际汽车制造技术与装备及材料展览会（2017.9.5~2017.9.8）上参展我们的装备及材料。上海国际汽车制造技术与装备及材料展览会作为亚洲最大的汽车材料与设计、工艺与装备、质量与装配、工程与服务技术行业盛会，上海国际汽车制造技术与装备及材料展览会（<strong>AMTS</strong>）自2004年创办以来，以国际化的视角呈现汽车工程领域全方位的系统集成解决方案。10多年来，展会的规模、展示范围以及专业观众快速增长，极大地推动了国际汽车制造装备技术交流及贸易的发展！</span></p><p style="text-indent: 2em; line-height: 3em;"><span style="font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;"><strong>AMTS</strong> 2017第十三届上海国际汽车制造技术与装备及材料展览会将于2017年9月5-8日在上海新国际博览中心举行，将汇聚来自中国/亚洲的知名汽车制造商和一级供应商的工程技术人员和决策者到场参观，预计将有来自世界各地800家展商参展，展示面积首次突破80,000平方米，吸引80,000专业观众到场参观。</span></p><p style="line-height: 2em; text-indent: 2em;"><span style="font-family: 微软雅黑,Microsoft YaHei; font-size: 16px;"><br/></span></p><p style="text-align:center"><span style=";font-family: 宋体;font-size: 14px"><img title="1501206324395031.png" alt="汽展.png" src="/ueditor/php/upload/image/20170728/1501206324395031.png"/></span></p><p style="text-indent: 2em; line-height: 3em;"><span style=";font-family: 宋体;font-size: 14px"></span><br/></p><p style="margin: 0px; text-align: left; text-indent: 2em; line-height: 3em;"><span style="font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;">我司届时通过展示汽车核心部件成形工程、冷热处理到打磨抛光修边、质量控制及检测、连接、组装及测试、部件清洁及标识、数控夹具到自动上下料机器人系统应用等，提供满足从单件加工到大批量生产，以及全自动整体解决方案，功能测试用快速连接器；并提供发动机总成油道、水道气密测试解决方案；燃油系统气密性及通气性检测方案；汽车零部件气密检测；胶管、燃气行业以及医疗器械等领域的气密性检测解决方案。有意者欢迎前来洽谈。</span></p><p style="line-height: 2em;"><span style=";font-family: 宋体;font-size: 14px"></span><br/></p>', '0', '371', '2017-07-28', '', '', '', 'info', '', '', '0', '3633', '', '', '1583119400', '0', '', '', '', '1', '', '', '', '');
